CREATE OR REPLACE PROCEDURE adicionar_pasta_report_dar ( nr_seq_pai_p tree_report_dar.nr_seq_pai%TYPE default null, ie_tipo_p tree_report_dar.ie_tipo%TYPE DEFAULT NULL, ds_titulo_p tree_report_dar.ds_titulo%TYPE DEFAULT NULL) AS $body$
DECLARE


	nr_seq_ordem_w        varchar(10);


BEGIN

if (coalesce(nr_seq_pai_p::text, '') = '') then
    select coalesce(max(nr_seq_ordem), 0) + 1
    into STRICT nr_seq_ordem_w 
    from tree_report_dar 
    where nr_seq_pai = nr_seq_pai_p;
else
	select coalesce(max(nr_seq_ordem), 0) + 1
	into STRICT nr_seq_ordem_w
	from tree_report_dar
	where coalesce(nr_seq_pai::text, '') = '';
end if;

insert into tree_report_dar(
	nr_sequencia,
    nm_usuario,
    dt_atualizacao,
    nr_seq_pai,
    ie_tipo,
    ds_titulo,
	nr_seq_ordem)
values (
	nextval('tree_report_dar_seq'),
	wheb_usuario_pck.get_nm_usuario,
	clock_timestamp(),
	nr_seq_pai_p,
	ie_tipo_p,
	ds_titulo_p,
	nr_seq_ordem_w
	);

end;
$body$
LANGUAGE PLPGSQL
