CREATE OR REPLACE PROCEDURE adicionar_query_generica ( nr_seq_pai_p tree_query_dar.nr_seq_pai%TYPE, ie_tipo_p tree_query_dar.ie_tipo%TYPE, ie_query_folder_dtm_p tree_query_dar.ie_query_folder_dtm%TYPE, ds_titulo_p tree_query_dar.ds_titulo%TYPE, nr_seq_filter_p tree_query_dar.nr_seq_filter%TYPE, code_p INOUT tree_query_dar.nr_sequencia%type) AS $body$
