CREATE OR REPLACE PROCEDURE adicionar_registro_template ( nr_seq_registro_p bigint, ds_lista_templates_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_templates_w	varchar(2000);
ds_lista_prontuario_w	varchar(2000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_template_w	bigint;
nr_seq_item_pep_w	bigint;
nr_seq_reg_template_w	bigint;
nr_seq_reg_item_w	bigint;
nr_seq_apres_w		integer;


BEGIN
if (nr_seq_registro_p IS NOT NULL AND nr_seq_registro_p::text <> '') and (ds_lista_templates_p IS NOT NULL AND ds_lista_templates_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	ds_lista_templates_w	:= ds_lista_templates_p;

	while	(ds_lista_templates_w IS NOT NULL AND ds_lista_templates_w::text <> '') loop
		begin
		tam_lista_w	:= length(ds_lista_templates_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_templates_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_template_w	:= (substr(ds_lista_templates_w, 1, (ie_pos_virgula_w - 1)))::numeric;
			ds_lista_templates_w	:= substr(ds_lista_templates_w, (ie_pos_virgula_w + 1), tam_lista_w);
		else
			nr_seq_template_w	:= ds_lista_templates_w;
			ds_lista_templates_w	:= null;
		end if;

		if (nr_seq_template_w > 0) then


			select	nextval('ehr_reg_template_seq')
			into STRICT	nr_seq_reg_template_w
			;

			insert into ehr_reg_template(
				nr_sequencia,
				nr_seq_reg,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_registro,
				nr_seq_template,
				nr_seq_item_pep,
				dt_liberacao,
				nr_seq_apres)
			values (
				nr_seq_reg_template_w,
				nr_seq_registro_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_template_w,
				null,
				null,
				nr_seq_apres_w);

		end if;
		end;
	end loop;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
