SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adicionar_sus_apac_proc ( nr_seq_apac_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w				bigint;
cd_procedimento_w			bigint;
ie_origem_proced_w			bigint;
cd_atividade_prof_bpa_w			smallint;
qt_procedimento_w			double precision;
nr_atendimento_w			bigint;
nr_seq_propaci_w			bigint;

C01 CURSOR FOR 
SELECT	a.cd_procedimento, 
	a.ie_origem_proced, 
	a.cd_atividade_prof_bpa, 
	a.qt_procedimento, 
	a.nr_atendimento, 
	a.nr_sequencia 
from	procedimento_paciente a, 
	conta_paciente c 
where	c.nr_atendimento	= nr_atendimento_w 
and	a.nr_interno_conta	= c.nr_interno_conta 
and	a.ie_origem_proced	= 3 
and	coalesce(c.nr_seq_protocolo::text, '') = '' 
and	coalesce(c.ie_cancelamento::text, '') = '' 
and	coalesce(a.cd_motivo_exc_conta::text, '') = '' 
and	not exists (SELECT	1 
	from	sus_apac_proc b 
	where	b.nr_seq_propaci = a.nr_sequencia);


BEGIN 
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_w 
from	sus_apac_movto 
where	nr_sequencia	= nr_seq_apac_p;
 
open C01;
loop 
fetch c01 into 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	cd_atividade_prof_bpa_w, 
	qt_procedimento_w, 
	nr_atendimento_w, 
	nr_seq_propaci_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	BEGIN 
 
	/* Buscar sequencia da sus_apac_proc */
	 
	select	nextval('sus_apac_proc_seq') 
	into STRICT	nr_sequencia_w 
	;
 
	/* Incluir na sus_apac_proc */
	 
	insert into 	sus_apac_proc( 
		NR_SEQUENCIA, 
		NR_SEQ_APAC, 
		CD_PROCEDIMENTO, 
		IE_ORIGEM_PROCED, 
		CD_ATIVIDADE_PROF, 
		QT_PROCEDIMENTO, 
		DT_ATUALIZACAO, 
		NM_USUARIO, 
		CD_CGC_FORNECEDOR, 
		NR_NOTA_FISCAL, 
		NR_SEQ_PROPACI) 
	values ( 
		nr_sequencia_w, 
		nr_seq_apac_p, 
		cd_procedimento_w, 
		ie_origem_proced_w, 
		cd_atividade_prof_bpa_w,		 
		qt_procedimento_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		null, 
		null, 
		nr_seq_propaci_w);
	END;
end loop;
close C01;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adicionar_sus_apac_proc ( nr_seq_apac_p bigint, nm_usuario_p text) FROM PUBLIC;

