CREATE OR REPLACE PROCEDURE adicionar_usuario_por_perfil ( cd_perfil_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text) AS $body$
DECLARE


insert_usuario_w boolean := true;

C01 CURSOR FOR
	SELECT 	a.nm_usuario
	from	usuario_perfil a, usuario b
	where	a.cd_perfil = cd_perfil_p
	and	a.nm_usuario = b.nm_usuario
	and	coalesce(b.dt_inativacao::text, '') = '';
c01_w	c01%rowtype;

C02 CURSOR FOR
	SELECT 	nm_usuario_grupo
	from	usuario_grupo
	where	nr_seq_grupo = nr_seq_grupo_p;
c02_w	c02%rowtype;


BEGIN

open C01;
loop
fetch C01 into c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert_usuario_w := true;

	open C02;
	loop
	fetch C02 into c02_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		if (c01_w.nm_usuario = c02_w.nm_usuario_grupo)then
			insert_usuario_w := false;
		end if;

		end;
	end loop;
	close C02;

	if (insert_usuario_w)then
		insert into usuario_grupo(nr_sequencia,
			nr_seq_grupo,
			dt_atualizacao,
			nm_usuario,
			nm_usuario_grupo,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_situacao)
		values (nextval('usuario_grupo_seq'),
			nr_seq_grupo_p,
			clock_timestamp(),
			nm_usuario_p,
			c01_w.nm_usuario,
			clock_timestamp(),
			nm_usuario_p,
			'A');
	end if;

	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
