CREATE OR REPLACE PROCEDURE adic_ativ_cron_proj_migr ( cd_funcao_p bigint, nr_seq_cronograma_p bigint, ie_atividade_p text, nm_atividade_p text, ie_gerar_fase_p text, ie_nova_atividade_p text, qt_tempo_nova_atividade_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_tempo_atividade_w	bigint;
nm_atividade_pai_w	varchar(255);
nr_seq_fase_adicionar_w	bigint;
ie_atividade_w		varchar(15);
ie_ativ_cron_w		varchar(1) := 'N';


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') and (ie_atividade_p IS NOT NULL AND ie_atividade_p::text <> '') and (nm_atividade_p IS NOT NULL AND nm_atividade_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_ativ_cron_w
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and	ds_atividade = nm_atividade_p;

	if (ie_ativ_cron_w = 'N') then
		begin
		select	coalesce(max(qt_tempo_desenv),0)
		into STRICT	qt_tempo_atividade_w
		from	w_analise_migr_proj
		where	cd_funcao = cd_funcao_p
		and	nm_objeto = nm_atividade_p;

		if (qt_tempo_atividade_w = 0) and (ie_nova_atividade_p = 'S') and (qt_tempo_nova_atividade_p > 0) then
			begin
			qt_tempo_atividade_w := qt_tempo_nova_atividade_p;
			end;
		end if;

		if (qt_tempo_atividade_w > 0) then
			begin
			if (ie_atividade_p in ('TTabSheet','TMenuItem')) then
				begin
				select	max(nm_objeto_pai)
				into STRICT	nm_atividade_pai_w
				from	w_analise_migr_proj
				where	cd_funcao = cd_funcao_p
				and	nm_objeto = nm_atividade_p;

				select	coalesce(min(nr_sequencia),0)
				into STRICT	nr_seq_fase_adicionar_w
				from	proj_cron_etapa
				where	nr_seq_cronograma = nr_seq_cronograma_p
				and	ie_fase = 'S'
				and	ie_tipo_obj_proj_migr = ie_atividade_p
				and	ds_atividade = nm_atividade_pai_w;

				if (nr_seq_fase_adicionar_w = 0) and (ie_gerar_fase_p = 'S') then
					begin
					CALL adicionar_fase_cron_proj_migr(
						nr_seq_cronograma_p,
						ie_atividade_p,
						nm_atividade_pai_w,
						nm_usuario_p);

					select	coalesce(min(nr_sequencia),0)
					into STRICT	nr_seq_fase_adicionar_w
					from	proj_cron_etapa
					where	nr_seq_cronograma = nr_seq_cronograma_p
					and	ie_fase = 'S'
					and	ie_tipo_obj_proj_migr = ie_atividade_p
					and	ds_atividade = nm_atividade_pai_w;
					end;
				end if;
				end;
			else
				begin
				select	coalesce(min(nr_sequencia),0)
				into STRICT	nr_seq_fase_adicionar_w
				from	proj_cron_etapa
				where	nr_seq_cronograma = nr_seq_cronograma_p
				and	ie_fase = 'S'
				and	ie_tipo_obj_proj_migr = ie_atividade_p;
				end;
			end if;

			if (nr_seq_fase_adicionar_w > 0) then
				begin
				if (ie_atividade_p = 'TDBGrid') then
					begin
					ie_atividade_w := 'TWCPanel';
					end;
				else
					begin
					ie_atividade_w := ie_atividade_p;
					end;
				end if;

				CALL adicionar_ativ_cron_proj_migr(
					ie_atividade_w,
					nm_atividade_p,
					qt_tempo_atividade_w,
					nr_seq_fase_adicionar_w,
					nm_usuario_p);
				end;
			else
				begin
				CALL wheb_mensagem_pck.exibir_mensagem_abort(282259);
				end;
			end if;
			end;
		else
			begin
			CALL wheb_mensagem_pck.exibir_mensagem_abort(282260);
			end;
		end if;
		end;
	else
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(282262);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
