CREATE OR REPLACE PROCEDURE adic_ativ_extra_cron_proj_migr ( cd_funcao_p bigint, nr_seq_cronograma_p bigint, ie_atividade_p text, nm_atividade_p text, qt_tempo_nova_atividade_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_fase_adicionar_w	bigint;


BEGIN
if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') and (ie_atividade_p IS NOT NULL AND ie_atividade_p::text <> '') and (nm_atividade_p IS NOT NULL AND nm_atividade_p::text <> '') and (qt_tempo_nova_atividade_p IS NOT NULL AND qt_tempo_nova_atividade_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(min(nr_sequencia),0)
	into STRICT	nr_seq_fase_adicionar_w
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and	ie_fase = 'S'
	and	ie_tipo_obj_proj_migr = 'OC';

	if (nr_seq_fase_adicionar_w = 0) then
		begin
		CALL adic_fase_extra_cron_proj_migr(
			nr_seq_cronograma_p,
			'OC',
			'Outros Componentes',
			nm_usuario_p);

		select	coalesce(min(nr_sequencia),0)
		into STRICT	nr_seq_fase_adicionar_w
		from	proj_cron_etapa
		where	nr_seq_cronograma = nr_seq_cronograma_p
		and	ie_fase = 'S'
		and	ie_tipo_obj_proj_migr = 'OC';
		end;
	end if;

	CALL adicionar_ativ_cron_proj_migr(
		ie_atividade_p,
		nm_atividade_p,
		qt_tempo_nova_atividade_p,
		nr_seq_fase_adicionar_w,
		nm_usuario_p);
	end;
end if;
commit;
end;
$body$
