SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adic_rem_zeros_esq_retorno_js ( nr_sequencia_p bigint, qt_digitos_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(ie_opcao_p, -1) = 0) then

	update	convenio_retorno_movto
	set	nr_doc_convenio	= ltrim(nr_doc_convenio, '0'),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p
	and	(nr_doc_convenio IS NOT NULL AND nr_doc_convenio::text <> '');

elsif (coalesce(ie_opcao_p, -1) = 1) then

	update	convenio_retorno_movto
	set	nr_doc_convenio   = lpad(nr_doc_convenio, qt_digitos_p, '0'),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia      = nr_sequencia_p
	and	(nr_doc_convenio IS NOT NULL AND nr_doc_convenio::text <> '')
	and	length(nr_doc_convenio) <= qt_digitos_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
