CREATE OR REPLACE PROCEDURE administrar_medic_quimio_palm ( nr_seq_atendimento_p bigint, nr_seq_p bigint, nm_usuario_p text, ie_opcao_p text, ie_tipo_seq_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE



qtd_w		    bigint	:= 0;
ie_consiste_sinal_vital varchar(1);
nr_atendimento_w	    bigint;


BEGIN
CALL alterar_nls_sessao();

select	coalesce(max(Obter_Valor_Param_Usuario(88, 69 , Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_p)), 'S')
into STRICT	ie_consiste_sinal_vital
;

if (ie_consiste_sinal_vital = 'S') then
	select	max(nr_atendimento)
	into STRICT 	nr_atendimento_w
	from	paciente_atendimento
	where	nr_seq_atendimento = nr_seq_atendimento_p;

	select coalesce(max('S'),'N')
	into STRICT   ie_consiste_sinal_vital
	from   atendimento_sinal_vital
	where  nr_atendimento = nr_atendimento_w;

	if (ie_consiste_sinal_vital = 'N') then
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(237883);
	end if;
end  if;

select	count(*)
into STRICT	qtd_w
from	paciente_atendimento
where	nr_seq_atendimento 	= nr_seq_atendimento_p
and	(dt_inicio_adm IS NOT NULL AND dt_inicio_adm::text <> '');

if (qtd_w = 0) then
	update 	paciente_atendimento
	set	dt_inicio_adm		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_atendimento 	= nr_seq_atendimento_p;
end if;

CALL alterar_status_medic_quimio(nr_seq_atendimento_p, nr_seq_p,nm_usuario_p, ie_opcao_p, ie_tipo_seq_p, cd_estabelecimento_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
