CREATE OR REPLACE PROCEDURE administrativo_tef ( cd_sitef_p text, nr_autorizacao_p text, vl_transacao_p bigint, nr_retorno_p bigint, nm_usuario_p text, nr_seq_movto_cartao_p INOUT bigint) AS $body$
DECLARE

		 
nr_seq_bandeira_w 	bigint;
nr_seq_movto_cartao_w	bigint;		
 

BEGIN 
if (cd_sitef_p IS NOT NULL AND cd_sitef_p::text <> '') then 
	begin 
	select 	max(nr_sequencia) 
	into STRICT	nr_seq_bandeira_w 
	from 	bandeira_cartao_cr 
	where 	cd_sitef = cd_sitef_p;
	 
	if (nr_seq_bandeira_w IS NOT NULL AND nr_seq_bandeira_w::text <> '') then 
		begin 
		select 	max(nr_sequencia) 
		into STRICT 	nr_seq_movto_cartao_w 
		from  	movto_cartao_cr 
		where 	nr_seq_bandeira = nr_seq_bandeira_w 
		and  	lpad(ds_comprovante,10,'0') = lpad(nr_autorizacao_p, 10, '0') 
		and  	vl_transacao  = vl_transacao_p;
		end;
	else 
		begin 
		select 	max(nr_sequencia) 
		into STRICT 	nr_seq_movto_cartao_w 
        from  	movto_cartao_cr 
        where  lpad(ds_comprovante,10,'0') = lpad(nr_autorizacao_p, 10,'0') 
		and  	vl_transacao  = vl_transacao_p;
		end;
	end if;
 
	if (nr_seq_movto_cartao_w IS NOT NULL AND nr_seq_movto_cartao_w::text <> '') then 
		begin 
		if (nr_retorno_p = 0) then 
			begin 
			CALL cancelar_movto_cartao_tef(nr_seq_movto_cartao_w, nm_usuario_p,'');
			end;
		end if;
		end;
	end if;
	end;
end if;
nr_seq_movto_cartao_p := nr_seq_movto_cartao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
