CREATE OR REPLACE PROCEDURE adm_liberar_perfil ( nm_usuario_registro_p text, nm_usuario_p text) AS $body$
DECLARE


cd_perfil_w		bigint;

C01 CURSOR FOR
	SELECT distinct a.cd_perfil
	from    cargo_perfil a,
		pessoa_fisica b,
		usuario c
	where   a.cd_cargo = b.cd_cargo
	and     b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and	c.nm_usuario  = nm_usuario_registro_p
	and	not exists ( 	SELECT 1
				from   usuario_perfil x
				where  x.nm_usuario = c.nm_usuario
				and    x.cd_perfil  = a.cd_perfil);


BEGIN

commit;

open C01;
loop
fetch C01 into
	cd_perfil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into usuario_perfil(
			nm_usuario,
			cd_perfil,
			dt_atualizacao,
			nm_usuario_atual )
		values (
			nm_usuario_registro_p,
			cd_perfil_w,
			clock_timestamp(),
			nm_usuario_p );
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
