CREATE OR REPLACE PROCEDURE adm_liberar_setor ( nm_usuario_registro_p text, nm_usuario_p text) AS $body$
DECLARE


cd_setor_atendimento_w	bigint;

C01 CURSOR FOR
	SELECT distinct a.cd_setor_atendimento
	from    cargo_setor a,
		pessoa_fisica b,
		usuario c
	where   a.cd_cargo = b.cd_cargo
	and     b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and	c.nm_usuario  = nm_usuario_registro_p
	and	not exists ( 	SELECT 1
				from   usuario_setor x
				where  x.nm_usuario = c.nm_usuario
				and    x.cd_setor_atendimento  = a.cd_setor_atendimento);


BEGIN

commit;

open C01;
loop
fetch C01 into
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into usuario_setor(
			nm_usuario_param,
			cd_setor_atendimento,
			dt_atualizacao,
			nm_usuario)
		values (	nm_usuario_registro_p,
			cd_setor_atendimento_w,
			clock_timestamp(),
			nm_usuario_p);
	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
