CREATE OR REPLACE PROCEDURE adm_medic_adep_quimio ( nr_seq_atendimento_p bigint, nr_atendimento_p bigint, dt_referencia_p timestamp, nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_acao_p text, nr_seq_horario_p bigint) AS $body$
DECLARE


nr_sequencia_w		prescr_mat_hor.nr_sequencia%type;
nr_seq_alteracao_w	prescr_mat_alteracao.nr_sequencia%type;
nr_seq_material_w	prescr_material.nr_sequencia%type;
ie_acm_sn_w			prescr_material.ie_acm%type;
cd_material_w		prescr_material.cd_material%type;
qt_dose_w			prescr_material.qt_dose%type;
ie_agrupador_w		prescr_material.ie_agrupador%type;
nr_atendimento_w	prescr_medica.nr_atendimento%type;
dt_horario_w		prescr_mat_hor.dt_horario%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
			a.nr_seq_material,
			obter_se_acm_sn(b.ie_acm, b.ie_se_necessario),
			b.cd_material,
			b.qt_dose,
			a.ie_agrupador,
			a.nr_atendimento,
			a.dt_horario
	from	prescr_mat_hor a,
			prescr_material b
	where	a.nr_prescricao = nr_prescricao_p
	and		a.nr_prescricao = b.nr_prescricao
	and		b.nr_sequencia = a.nr_seq_material
	and		b.nr_seq_atendimento = nr_seq_atendimento_p
	and		coalesce(coalesce(a.dt_fim_horario, a.dt_suspensao)::text, '') = ''
	and		ie_acao_p = 'A';

C02 CURSOR FOR
	SELECT	a.nr_sequencia,
			a.nr_seq_material,
			obter_se_acm_sn(b.ie_acm, b.ie_se_necessario),
			b.cd_material,
			b.qt_dose,
			a.ie_agrupador,
			a.nr_atendimento,
			a.dt_horario
	from	prescr_mat_hor a,
			prescr_material b
	where	a.nr_prescricao = nr_prescricao_p
	and		a.nr_prescricao = b.nr_prescricao
	and		b.nr_sequencia = a.nr_seq_material
	and		b.nr_seq_atendimento = nr_seq_atendimento_p
	and		(a.dt_fim_horario IS NOT NULL AND a.dt_fim_horario::text <> '')
	and		ie_acao_p = 'R';




BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_material_w,
	ie_acm_sn_w,
	cd_material_w,
	qt_dose_w,
	ie_agrupador_w,
	nr_atendimento_w,
	dt_horario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	prescr_mat_hor
	set		dt_fim_horario = clock_timestamp(),
			nm_usuario_adm = nm_usuario_p
	where	nr_sequencia = nr_sequencia_w
	and		coalesce(coalesce(dt_fim_horario, dt_suspensao)::text, '') = '';

	select	nextval('prescr_mat_alteracao_seq')
	into STRICT	nr_seq_alteracao_w
	;

	insert into prescr_mat_alteracao(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_prescricao,
			nr_seq_prescricao,
			dt_alteracao,
			cd_pessoa_fisica,
			ie_alteracao,
			nr_atendimento,
			qt_dose_original,
			ie_acm_sn,
			cd_item,
			ie_mostra_adep,
			ie_tipo_item,
			dt_horario_acao
			)
	values (
			nr_seq_alteracao_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p,
			nr_seq_material_w,
			clock_timestamp(),
			obter_dados_usuario_opcao(nm_usuario_p,'C'),
			3,
			nr_atendimento_w,
			qt_dose_w,
			ie_acm_sn_w,
			cd_material_w,
			'S',
			CASE WHEN ie_agrupador_w=2 THEN  'MAT'  ELSE 'M' END ,
			dt_horario_w
			);


	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_sequencia_w,
	nr_seq_material_w,
	ie_acm_sn_w,
	cd_material_w,
	qt_dose_w,
	ie_agrupador_w,
	nr_atendimento_w,
	dt_horario_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	update	prescr_mat_hor
	set		dt_fim_horario  = NULL,
			nm_usuario_adm  = NULL
	where	nr_sequencia = nr_sequencia_w
	and		(coalesce(dt_fim_horario, dt_suspensao) IS NOT NULL AND (coalesce(dt_fim_horario, dt_suspensao))::text <> '');

	select	nextval('prescr_mat_alteracao_seq')
	into STRICT	nr_seq_alteracao_w
	;

	insert into prescr_mat_alteracao(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_prescricao,
			nr_seq_prescricao,
			dt_alteracao,
			cd_pessoa_fisica,
			ie_alteracao,
			nr_atendimento,
			qt_dose_original,
			ie_acm_sn,
			cd_item,
			ie_mostra_adep,
			ie_tipo_item,
			dt_horario_acao
			)
	values (
			nr_seq_alteracao_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p,
			nr_seq_material_w,
			clock_timestamp(),
			obter_dados_usuario_opcao(nm_usuario_p,'C'),
			4,
			nr_atendimento_w,
			qt_dose_w,
			ie_acm_sn_w,
			cd_material_w,
			'S',
			CASE WHEN ie_agrupador_w=2 THEN  'MAT'  ELSE 'M' END ,
			dt_horario_w
			);


	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adm_medic_adep_quimio ( nr_seq_atendimento_p bigint, nr_atendimento_p bigint, dt_referencia_p timestamp, nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_acao_p text, nr_seq_horario_p bigint) FROM PUBLIC;

