SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE adps_gerar_pagto_itau_cnab_240 ( nr_seq_envio_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* PADRÃO CNAB 240 VERSÃO 080 DE 07/2010 */

ds_conteudo_w		varchar(240);
cd_estabelecimento_w	smallint;
nr_seq_apres_w		bigint	:= 0;

/* header de arquivo */

cd_agencia_estab_w	varchar(5);
cd_cgc_estab_w		varchar(14);
cd_convenio_banco_w	varchar(20);
dt_geracao_w		varchar(14);
nm_empresa_w		varchar(30);
nr_conta_estab_w	varchar(12);
nr_remessa_w		bigint;
ie_digito_estab_w	varchar(1);

/* header de lote - DOC */

nr_lote_servico_w	bigint;
ie_forma_lanc_w		varchar(2);
ie_tipo_pagamento_w	varchar(3);
ie_finalidade_w		varchar(2);
ds_endereco_w		varchar(30);
nr_endereco_w		varchar(5);
ds_complemento_w	varchar(15);
ds_municipio_w		varchar(20);
nr_cep_w		varchar(8);
sg_estado_w		varchar(15);
ie_converte_bloq_w	varchar(1);
ie_tipo_pagto_w		varchar(2);
nr_versao_w		varchar(3);

c01 CURSOR FOR
SELECT	distinct
	CASE WHEN b.ie_tipo_pagamento='CC' THEN '01' WHEN b.ie_tipo_pagamento='DOC' THEN '03' WHEN b.ie_tipo_pagamento='TED' THEN '41' WHEN b.ie_tipo_pagamento='OP' THEN '30' WHEN b.ie_tipo_pagamento='PC' THEN '13' END ,
	b.ie_tipo_pagamento,
	CASE WHEN b.ie_tipo_pagamento='CC' THEN '01'  ELSE '99' END ,
	'20' ie_tipo_pagto
from	titulo_pagar_escrit b,
	banco_escritural a
where	b.ie_tipo_pagamento	in ('DOC','CC','OP','TED','PC')
and	a.nr_sequencia		= b.nr_seq_escrit
and	a.nr_sequencia		= nr_seq_envio_p;

/* detalhe - DOC */

nr_sequencia_w		bigint;
cd_camara_compensacao_w	varchar(3);
cd_banco_w		smallint;
cd_agencia_bancaria_w	varchar(6);
nr_conta_w		varchar(20);
nm_pessoa_w		varchar(30);
nr_titulo_w		bigint;
dt_remessa_retorno_w	timestamp;
vl_escritural_w		double precision;
vl_acrescimo_w		double precision;
vl_desconto_w		double precision;
vl_despesa_w		double precision;
ie_tipo_inscricao_w	varchar(1);
nr_inscricao_w		varchar(14);
dt_vencimento_w		timestamp;
vl_juros_w		double precision;
vl_multa_w		double precision;
cd_agencia_conta_w	varchar(20);
ie_digito_agencia_w	varchar(2);
cd_agencia_w		varchar(8);
nr_nosso_numero_w	varchar(15);
ie_digito_conta_w	varchar(1);

c02 CURSOR FOR
SELECT	b.cd_banco,
	lpad(substr(b.cd_agencia_bancaria,1,5) || substr(b.ie_digito_agencia,1,1),6,'0'),
	rpad(substr(obter_nome_pf_pj(c.cd_pessoa_fisica,c.cd_cgc),1,30),30,' '),
	c.nr_titulo,
	b.vl_escritural,
	b.vl_acrescimo,
	b.vl_desconto,
	b.vl_despesa,
	lpad(coalesce(d.nr_cpf,c.cd_cgc),14,'0'),
	b.nr_conta,
	lpad(coalesce(substr(b.ie_digito_agencia,1,2),' '),2,' '),
	b.cd_agencia_bancaria,
	rpad(substr(c.nr_nosso_numero,1,15),15,' ') nr_nosso_numero,
	c.dt_vencimento_atual,
	lpad(coalesce(substr(b.ie_digito_conta,1,1),' '),1,' '),
	c.nr_bloqueto,
	b.vl_juros,
	b.vl_multa
FROM titulo_pagar_escrit b, banco_escritural a, titulo_pagar c
LEFT OUTER JOIN pessoa_fisica d ON (c.cd_pessoa_fisica = d.cd_pessoa_fisica)
WHERE b.nr_titulo		= c.nr_titulo and b.ie_tipo_pagamento	= ie_tipo_pagamento_w and a.nr_sequencia		= b.nr_seq_escrit and a.nr_sequencia		= nr_seq_envio_p;

/* trailer lote - DOC */

vl_total_w		double precision;

/* header de lote - BLQ */

ie_tipo_bloqueto_w	varchar(1);

c03 CURSOR FOR
SELECT	distinct
	CASE WHEN substr(c.nr_bloqueto,1,3)='341' THEN '30'  ELSE '31' END ,
	b.ie_tipo_pagamento,
	CASE WHEN(SELECT	count(*)		from	banco x		where	x.cd_banco	= somente_numero(substr(c.nr_bloqueto,1,3)))=0 THEN		'I'  ELSE 'B' END  ie_tipo_bloqueto,
	'20' ie_tipo_pagto
from	titulo_pagar c,
	titulo_pagar_escrit b,
	banco_escritural a
where	b.nr_titulo		= c.nr_titulo
and	b.ie_tipo_pagamento	= 'BLQ'
and	a.nr_sequencia		= b.nr_seq_escrit
and	a.nr_sequencia		= nr_seq_envio_p;

/* detalhe - BLQ */

nr_bloqueto_w		varchar(48);
nr_nota_fiscal_w	varchar(9);

c04 CURSOR FOR
SELECT	rpad(substr(obter_nome_pf_pj(c.cd_pessoa_fisica,c.cd_cgc),1,30),30,' '),
	c.nr_titulo,
	b.vl_escritural,
	b.vl_acrescimo,
	b.vl_desconto,
	b.vl_despesa,
	c.dt_vencimento_atual,
	b.vl_juros,
	b.vl_multa,
	substr(CASE WHEN ie_tipo_bloqueto_w='I' THEN c.nr_bloqueto  ELSE CASE WHEN coalesce(ie_converte_bloq_w,'N')='S' THEN converte_codigo_bloqueto('Lido_Barra',c.nr_bloqueto)  ELSE c.nr_bloqueto END  END ,1,48) nr_bloqueto,
	lpad(somente_numero(coalesce(d.nr_nota_fiscal,'0')),9,'0') nr_nota_fiscal
FROM titulo_pagar_escrit b, banco_escritural a, titulo_pagar c
LEFT OUTER JOIN nota_fiscal d ON (c.nr_seq_nota_fiscal = d.nr_sequencia)
WHERE b.nr_titulo		= c.nr_titulo and ie_forma_lanc_w		=
	CASE WHEN 	b.ie_tipo_pagamento='PC' THEN		'13'  ELSE CASE WHEN(SELECT	count(*)			from	banco x			where	x.cd_banco	= somente_numero(substr(c.nr_bloqueto,1,3)))=0 THEN 			'13'  ELSE CASE WHEN substr(c.nr_bloqueto,1,3)='341' THEN '30'  ELSE '31' END  END  END and b.ie_tipo_pagamento	= ie_tipo_pagamento_w and a.nr_sequencia		= b.nr_seq_escrit and a.nr_sequencia		= nr_seq_envio_p;

/* trailer do arquivo */

qt_registro_w		bigint;


BEGIN

delete	from w_envio_banco
where	nm_usuario	= nm_usuario_p;

/* header de arquivo */

nr_seq_apres_w		:= coalesce(nr_seq_apres_w,0) + 1;

select	lpad(b.cd_cgc,14,'0'),
	rpad(substr(c.cd_convenio_banco,1,20),20,' '),
	lpad(substr(c.cd_agencia_bancaria,1,5),5,'0'),
	lpad(somente_numero(substr(c.cd_conta,1,12)),12,'0'),
	rpad(substr(obter_nome_estabelecimento(b.cd_estabelecimento),1,30),30,' '),
	to_char(clock_timestamp(),'ddmmyyyyhhmiss'),
	coalesce(a.nr_remessa,a.nr_sequencia),
	b.cd_estabelecimento,
	a.dt_remessa_retorno,
	rpad(substr(obter_dados_pf_pj(null,b.cd_cgc,'R'),1,30),30,' '),
	lpad(substr(obter_dados_pf_pj(null,b.cd_cgc,'NR'),1,5),5,'0'),
	rpad(coalesce(substr(obter_dados_pf_pj(null,b.cd_cgc,'CO'),1,15),' '),15,' '),
	rpad(substr(obter_dados_pf_pj(null,b.cd_cgc,'CI'),1,20),20,' '),
	lpad(substr(obter_dados_pf_pj(null,b.cd_cgc,'CEP'),1,8),8,'0'),
	rpad(substr(obter_dados_pf_pj(null,b.cd_cgc,'UF'),1,2),2,' '),
	coalesce(c.ie_digito_conta,'0')
into STRICT	cd_cgc_estab_w,
	cd_convenio_banco_w,
	cd_agencia_estab_w,
	nr_conta_estab_w,
	nm_empresa_w,
	dt_geracao_w,
	nr_remessa_w,
	cd_estabelecimento_w,
	dt_remessa_retorno_w,
	ds_endereco_w,
	nr_endereco_w,
	ds_complemento_w,
	ds_municipio_w,
	nr_cep_w,
	sg_estado_w,
	ie_digito_estab_w
from	banco_estabelecimento c,
	estabelecimento b,
	banco_escritural a
where	a.nr_seq_conta_banco	= c.nr_sequencia
and	a.cd_estabelecimento	= b.cd_estabelecimento
and	a.nr_sequencia		= nr_seq_envio_p;

ie_converte_bloq_w := obter_param_usuario(857, 36, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_converte_bloq_w);

ds_conteudo_w	:=	'341' ||
			'0000' ||
			'0' ||
			rpad(' ',6,' ') ||
			'080' ||
			'2' ||
			cd_cgc_estab_w ||
			rpad(' ',20,' ') ||
			cd_agencia_estab_w ||
			' ' ||
			nr_conta_estab_w ||
			' ' ||
			ie_digito_estab_w ||
			nm_empresa_w ||
			rpad('Banco Itaú',30,' ') ||
			rpad(' ',10,' ') ||
			'1' ||
			dt_geracao_w ||
			'000000000' ||
			'00000' ||
			rpad(' ',69,' ');

insert	into w_envio_banco(cd_estabelecimento,
	ds_conteudo,
	dt_atualizacao,
	nm_usuario,
	nr_seq_apres,
	nr_seq_apres_2,
	nr_sequencia)
values (cd_estabelecimento_w,
	ds_conteudo_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_apres_w,
	nr_seq_apres_w,
	nextval('w_envio_banco_seq'));

open	c01;
loop
fetch	c01 into
	ie_forma_lanc_w,
	ie_tipo_pagamento_w,
	ie_finalidade_w,
	ie_tipo_pagto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	/* header de lote */

	nr_lote_servico_w	:= coalesce(nr_lote_servico_w,0) + 1;
	nr_seq_apres_w		:= coalesce(nr_seq_apres_w,0) + 1;
	nr_sequencia_w		:= 0;
	vl_total_w		:= 0;

	if (ie_tipo_pagamento_w	= 'PC') then
		nr_versao_w	:= '030';
	else
		nr_versao_w	:= '040';
	end if;

	ds_conteudo_w	:=	'341' ||
				lpad(nr_lote_servico_w,4,'0') ||
				'1' ||
				'C' ||
				ie_tipo_pagto_w ||
				ie_forma_lanc_w ||
				nr_versao_w ||
				' ' ||
				'2' ||
				cd_cgc_estab_w ||
				rpad(' ',20,' ') ||
				cd_agencia_estab_w ||
				' ' ||
				nr_conta_estab_w ||
				' ' ||
				ie_digito_estab_w ||
				nm_empresa_w ||
				rpad(' ',40,' ') ||
				ds_endereco_w ||
				nr_endereco_w ||
				ds_complemento_w ||
				ds_municipio_w ||
				nr_cep_w ||
				substr(sg_estado_w,1,2) ||
				rpad(' ',18,' ');

	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

	open	c02;
	loop
	fetch	c02 into
		cd_banco_w,
		cd_agencia_bancaria_w,
		nm_pessoa_w,
		nr_titulo_w,
		vl_escritural_w,
		vl_acrescimo_w,
		vl_desconto_w,
		vl_despesa_w,
		nr_inscricao_w,
		nr_conta_w,
		ie_digito_agencia_w,
		cd_agencia_w,
		nr_nosso_numero_w,
		dt_vencimento_w,
		ie_digito_conta_w,
		nr_bloqueto_w,
		vl_juros_w,
		vl_multa_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		/* segmento A */

		nr_sequencia_w	:= coalesce(nr_sequencia_w,0) + 1;
		nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;
		qt_registro_w	:= coalesce(qt_registro_w,0) + 1;
		vl_total_w	:= coalesce(vl_total_w,0) + (coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0));

		if (ie_tipo_pagamento_w	= 'PC') then

			ds_conteudo_w	:=	'341' ||
						lpad(nr_lote_servico_w,4,'0') ||
						'3' ||
						lpad(nr_sequencia_w,5,'0') ||
						'O' ||
						'000' ||
						rpad(coalesce(nr_bloqueto_w,' '),48,' ') ||
						nm_pessoa_w ||
						to_char(dt_vencimento_w,'ddmmyyyy') ||
						'REA' ||
						'000000000000000' ||
						lpad(somente_numero(to_char(coalesce(vl_escritural_w,0),'9999999999990.00')),15,'0') ||
						to_char(dt_remessa_retorno_w,'ddmmyyyy') ||
						lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') ||
						rpad(' ',3,' ') ||
						'000000000' ||
						rpad(' ',3,' ') ||
						rpad(nr_titulo_w,20,' ') ||
						rpad(' ',21,' ') ||
						nr_nosso_numero_w ||
						rpad(' ',10,' ');

		else

			if (cd_banco_w in (341,409)) then

				cd_agencia_conta_w	:=	'0' ||
								lpad(substr(cd_agencia_w,1,4),4,'0') ||
								' ' ||
								'000000' ||
								lpad(substr(nr_conta_w,1,6),6,'0') ||
								' ' ||
								ie_digito_conta_w;

			else

				cd_agencia_conta_w	:=	lpad(substr(cd_agencia_w,1,5),5,'0') ||
								' ' ||
								lpad(substr(nr_conta_w,1,12),12,'0') ||
								' ' ||
								ie_digito_conta_w;

			end if;

			ds_conteudo_w	:=	'341' ||
						lpad(nr_lote_servico_w,4,'0') ||
						'3' ||
						lpad(nr_sequencia_w,5,'0') ||
						'A' ||
						'000' ||
						'000' ||
						lpad(cd_banco_w,3,'0') ||
						cd_agencia_conta_w ||
						nm_pessoa_w ||
						rpad(nr_titulo_w,20,' ') ||
						to_char(dt_remessa_retorno_w,'ddmmyyyy') ||
						'REA' ||
						'000000000000000' ||
						lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') ||
						rpad(' ',20,' ') ||
						'00000000' || /* to_char(dt_remessa_retorno_w,'ddmmyyyy') || */
						'000000000000000' || /* lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') || */
						rpad(' ',20,' ') ||
						'000000' ||
						nr_inscricao_w ||
						rpad(' ',23,' ');

		end if;

		insert	into w_envio_banco(cd_estabelecimento,
			ds_conteudo,
			dt_atualizacao,
			nm_usuario,
			nr_seq_apres,
			nr_seq_apres_2,
			nr_sequencia)
		values (cd_estabelecimento_w,
			ds_conteudo_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_apres_w,
			nr_seq_apres_w,
			nextval('w_envio_banco_seq'));

	end	loop;
	close	c02;

	/* trailer de lote */

	nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;

	ds_conteudo_w	:=	'341' ||
				lpad(nr_lote_servico_w,4,'0') ||
				'5' ||
				rpad(' ',9,' ') ||
				lpad(nr_sequencia_w + 2,6,'0') ||
				lpad(somente_numero(to_char(coalesce(vl_total_w,0),'9999999999999990.00')),18,'0') ||
				'000000000000000000' ||
				rpad(' ',181,' ');

	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

end	loop;
close	c01;

open	c03;
loop
fetch	c03 into
	ie_forma_lanc_w,
	ie_tipo_pagamento_w,
	ie_tipo_bloqueto_w,
	ie_tipo_pagto_w;
EXIT WHEN NOT FOUND; /* apply on c03 */

	if (ie_tipo_bloqueto_w	= 'I') then
		ie_forma_lanc_w	:= '19';
		ie_tipo_pagto_w	:= '22';
	end if;

	/* header de lote - BLQ Itaú */

	nr_lote_servico_w	:= coalesce(nr_lote_servico_w,0) + 1;
	nr_seq_apres_w		:= coalesce(nr_seq_apres_w,0) + 1;
	nr_sequencia_w		:= 0;
	vl_total_w		:= 0;

	ds_conteudo_w	:=	'341' ||
				lpad(nr_lote_servico_w,4,'0') ||
				'1' ||
				'C' ||
				ie_tipo_pagto_w ||
				ie_forma_lanc_w ||
				'030' ||
				' ' ||
				'2' ||
				cd_cgc_estab_w ||
				rpad(' ',20,' ') ||
				cd_agencia_estab_w ||
				' ' ||
				nr_conta_estab_w ||
				' ' ||
				ie_digito_estab_w ||
				nm_empresa_w ||
				rpad(' ',40,' ') ||
				ds_endereco_w ||
				nr_endereco_w ||
				ds_complemento_w ||
				ds_municipio_w ||
				nr_cep_w ||
				substr(sg_estado_w,1,2) ||
				rpad(' ',18,' ');

	
	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

	open	c04;
	loop
	fetch	c04 into
		nm_pessoa_w,
		nr_titulo_w,
		vl_escritural_w,
		vl_acrescimo_w,
		vl_desconto_w,
		vl_despesa_w,
		dt_vencimento_w,
		vl_juros_w,
		vl_multa_w,
		nr_bloqueto_w,
		nr_nota_fiscal_w;
	EXIT WHEN NOT FOUND; /* apply on c04 */

		nr_sequencia_w	:= coalesce(nr_sequencia_w,0) + 1;
		nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;
		qt_registro_w	:= coalesce(qt_registro_w,0) + 1;
		vl_total_w	:= coalesce(vl_total_w,0) + (coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_multa_w,0) + coalesce(vl_juros_w,0));

		/* segmento O */

		if (ie_tipo_bloqueto_w	= 'I') then

			ds_conteudo_w	:=	'341' ||
						lpad(nr_lote_servico_w,4,'0') ||
						'3' ||
						lpad(nr_sequencia_w,5,'0') ||
						'O' ||
						'000' ||
						nr_bloqueto_w ||
						nm_pessoa_w ||
						to_char(dt_vencimento_w,'ddmmyyyy') ||
						'REA' ||
						'000000000000000' ||
						lpad(somente_numero(to_char(coalesce(vl_escritural_w,0),'9999999999990.00')),15,'0') ||
						to_char(dt_remessa_retorno_w,'ddmmyyyy') ||
						lpad(somente_numero(to_char(coalesce(vl_multa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0),'9999999999990.00')),15,'0') ||
						rpad(' ',3,' ') ||
						rpad(' ',9,' ') ||
						rpad(' ',3,' ') ||
						rpad(nr_titulo_w,20,' ') ||
						rpad(' ',21,' ') ||
						rpad(' ',15,' ') ||
						rpad(' ',10,' ');

		/* segmento J */

		else

			ds_conteudo_w	:=	'341' ||
						lpad(nr_lote_servico_w,4,'0') ||
						'3' ||
						lpad(nr_sequencia_w,5,'0') ||
						'J' ||
						'000' ||
						nr_bloqueto_w ||
						nm_pessoa_w ||
						to_char(dt_vencimento_w,'ddmmyyyy') ||
						lpad(somente_numero(to_char(coalesce(vl_escritural_w,0),'9999999999990.00')),15,'0') ||
						lpad(somente_numero(to_char(coalesce(vl_desconto_w,0),'9999999999990.00')),15,'0') ||
						lpad(somente_numero(to_char(coalesce(vl_multa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0),'9999999999990.00')),15,'0') ||
						to_char(dt_remessa_retorno_w,'ddmmyyyy') ||
						lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_multa_w,0) + coalesce(vl_juros_w,0),'9999999999990.00')),15,'0') ||
						'000000000000000' ||
						rpad(nr_titulo_w,20,' ') ||
						rpad(' ',38,' ');

		end if;

		insert	into w_envio_banco(cd_estabelecimento,
			ds_conteudo,
			dt_atualizacao,
			nm_usuario,
			nr_seq_apres,
			nr_seq_apres_2,
			nr_sequencia)
		values (cd_estabelecimento_w,
			ds_conteudo_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_apres_w,
			nr_seq_apres_w,
			nextval('w_envio_banco_seq'));

	end	loop;
	close	c04;

	/* trailer de lote */

	nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;

	ds_conteudo_w	:=	'341' ||
				lpad(nr_lote_servico_w,4,'0') ||
				'5' ||
				rpad(' ',9,' ') ||
				lpad(nr_sequencia_w + 2,6,'0') ||
				lpad(somente_numero(to_char(coalesce(vl_total_w,0),'9999999999999990.00')),18,'0') ||
				'000000000000000000' ||
				rpad(' ',181,' ');

	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

end	loop;
close	c03;

/* trailer de arquivo */

nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;

ds_conteudo_w	:=	'341' ||
			'9999' ||
			'9' ||
			rpad(' ',9,' ') ||
			lpad(nr_lote_servico_w,6,'0') ||
			lpad(coalesce(qt_registro_w,0) + (coalesce(nr_lote_servico_w,0) * 2) + 2,6,'0') ||	/* registros detalhe + header e trailer dos lotes + header e trailer do arquivo */
			rpad(' ',211,' ');

insert	into w_envio_banco(cd_estabelecimento,
	ds_conteudo,
	dt_atualizacao,
	nm_usuario,
	nr_seq_apres,
	nr_seq_apres_2,
	nr_sequencia)
values (cd_estabelecimento_w,
	ds_conteudo_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_apres_w,
	nr_seq_apres_w,
	nextval('w_envio_banco_seq'));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adps_gerar_pagto_itau_cnab_240 ( nr_seq_envio_p bigint, nm_usuario_p text) FROM PUBLIC;

