SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE afip_gerar_prescr_procedimento ( nr_prescricao_p bigint, cd_exame_integracao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
dt_prescricao_w	timestamp;
cd_material_exame_w varchar(20);
cd_proc_exame_w			bigint;
ie_origem_proc_exame_w	bigint;
nr_seq_exame_ww			bigint;


BEGIN

select	dt_prescricao
into STRICT 	dt_prescricao_w
from 	prescr_medica
where 	nr_prescricao = nr_prescricao_p;

select	coalesce(max(nr_sequencia), 0) + 1
into STRICT	nr_sequencia_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p;

select	MAX(cd_procedimento),
		max(ie_origem_proced),
		max(nr_seq_exame)
into STRICT	cd_proc_exame_w,
		ie_origem_proc_exame_w,
		nr_seq_exame_ww
from	exame_laboratorio
where	coalesce(coalesce(Obter_Equipamento_Exame(nr_seq_exame,null,'AFIP'),cd_exame_integracao),cd_exame)= cd_exame_integracao_p;

select	max(b.cd_material_exame)
into STRICT 	cd_material_exame_w
from	material_exame_lab b,
		exame_lab_material a
where	a.nr_seq_material	= b.nr_sequencia
and		a.nr_seq_exame		= nr_seq_exame_ww
and		a.ie_prioridade = (SELECT min(ie_prioridade) from exame_lab_material where nr_seq_exame = a.nr_seq_exame)
and		a.ie_situacao		= 'A'
order by a.ie_prioridade;

if (nr_seq_exame_ww IS NOT NULL AND nr_seq_exame_ww::text <> '') and (cd_material_exame_w IS NOT NULL AND cd_material_exame_w::text <> '') then
	insert into prescr_procedimento(
		nr_prescricao,
		nr_sequencia,
		nr_seq_exame,
		cd_material_exame,
		cd_procedimento,
		ie_origem_proced,
		qt_procedimento,
		ie_urgencia,
		ie_suspenso,
		dt_prev_execucao,
		ie_status_atend,
		dt_atualizacao,
		nm_usuario,
		ie_origem_inf,
		nr_seq_interno,
		ie_avisar_result,
		cd_motivo_baixa)
	values (nr_prescricao_p,
		nr_sequencia_w,
		nr_seq_exame_ww,
		cd_material_exame_w,
		cd_proc_exame_w,
		ie_origem_proc_exame_w,
		1,
		'N',
		'N',
		dt_prescricao_w,
		5,
		clock_timestamp(),
		nm_usuario_p,
		'1',
		nextval('prescr_procedimento_seq'),
		'N',
		0);

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
