CREATE OR REPLACE PROCEDURE after_delete_doc_rev (nr_seq_doc_p bigint, nm_usuario_p text) AS $body$
DECLARE



dt_aprovacao_w	timestamp;
dt_validacao_w	timestamp;
dt_revisao_w	timestamp;
status_anterior_w varchar(1);
nr_sequencia_w	bigint;



BEGIN


select  a.nr_sequencia,
	a.dt_aprovacao ,
	a.dt_validacao ,
	a.dt_revisao
into STRICT 	nr_sequencia_w,
	dt_aprovacao_w,
	dt_validacao_w,
	dt_revisao_w
from 	QUA_DOC_REVISAO a
where 	nr_seq_doc = nr_seq_doc_p
and 	a.nr_sequencia = ( SELECT max(nr_sequencia)
				from QUA_DOC_REVISAO
				where nr_seq_doc = nr_seq_doc_p);

if (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then

status_anterior_w 	:= 'D';

elsif (dt_revisao_w IS NOT NULL AND dt_revisao_w::text <> '') then

status_anterior_w	:= 'R';
end if;

if (status_anterior_w <> '') then
begin
update	qua_documento
set	ie_status = status_anterior_w,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia = nr_seq_doc_p;

commit;
end;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
