SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE af_gravar_proc_adic_cirurgiao (nr_seq_atend_futuro_p atend_pac_futuro.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
cd_medico_w		pessoa_fisica.cd_medico%type;
ie_gravar_proc_adic_w	varchar(1);

C01 CURSOR FOR
	SELECT	cd_procedimento,
		nr_seq_proc_interno,
		ie_lado,
		ie_origem_proced
	from	atend_pac_proced_previsto a
	where	a.nr_seq_atend_futuro = nr_seq_atend_futuro_p
	and	a.ie_proc_principal = 'N'
	order by cd_procedimento;

BEGIN

if (nr_seq_atend_futuro_p IS NOT NULL AND nr_seq_atend_futuro_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_gravar_proc_adic_w
	from	atend_pac_proced_previsto a
	where	a.nr_seq_atend_futuro = nr_seq_atend_futuro_p
	and	ie_proc_principal = 'S';

	if (ie_gravar_proc_adic_w = 'S') then

		select	max(cd_medico)
		into STRICT	cd_medico_w
		from	atend_pac_futuro a
		where	nr_sequencia = nr_seq_atend_futuro_p;

		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	usuario
		where	UPPER(nm_usuario) = UPPER(nm_usuario_p);

		for r_C01_w in C01 loop
			begin
			insert into w_agenda_proc_adic(NR_SEQUENCIA, DT_ATUALIZACAO, NM_USUARIO,
							DT_ATUALIZACAO_NREC, NM_USUARIO_NREC, CD_PESSOA_FISICA,
							NR_SEQ_PROC_INTERNO, CD_MEDICO, IE_LADO,
							CD_CONVENIO, CD_CATEGORIA, CD_PROCEDIMENTO,
							IE_ORIGEM_PROCED, QT_MIN_PROCED, CD_PLANO,
							QT_PROCEDIMENTO)
						values (	nextval('w_agenda_proc_adic_seq'), clock_timestamp(), nm_usuario_p,
							clock_timestamp(), nm_usuario_p, cd_pessoa_fisica_w,
							r_c01_w.nr_seq_proc_interno, cd_medico_w, r_c01_w.ie_lado,
							null, null, r_c01_w.cd_procedimento,
							r_c01_w.ie_origem_proced, null, null,
							null);
			end;
		end loop;

		commit;

	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
