SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE agcons_atualiza_chamada_atend (nr_seq_agenda_p bigint, nm_usuario_p text, ie_gerando_atend_p text) AS $body$
DECLARE



nr_atendimento_w	bigint;
/*
ie_gerando_atend_p:
S - Atendimento gerado;
N - Atendimento n√£o gerado
*/
BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '')then

	select	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;

	if (coalesce(nr_atendimento_w::text, '') = '') and (ie_gerando_atend_p = 'S')then
		update	agenda_consulta
		set		dt_chamada_atend 	= clock_timestamp(),
				nm_usuario			= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_p;

		commit;
	else
		update	agenda_consulta
		set		dt_chamada_atend 	 = NULL,
				nm_usuario			= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_p;

		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
