SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE agcons_verf_agend_futuro ( cd_agenda_p bigint, dt_desejada_p timestamp, ds_consistencia_p INOUT text) AS $body$
DECLARE


qt_horarios_livres_w	bigint;
ds_mensagem_w		varchar(2000);


BEGIN
ds_consistencia_p := null;
-- verifica se existem horários livres na Agenda de acordo com a agenda que se está inserindo o paciente na Lista de espera;
select	count(*)
into STRICT	qt_horarios_livres_w
from 	agenda_consulta a
where	a.cd_agenda = cd_agenda_p
and	a.dt_agenda BETWEEN trunc(dt_desejada_p) AND fim_dia(dt_desejada_p)
and	a.ie_status_agenda = 'L';


-- caso existirem horários livres irá bloquear a inclusão do paciente na lista de espera devido aos horários livres que existem na agenda de destino
if (qt_horarios_livres_w > 0) then
	ds_mensagem_w := substr(wheb_mensagem_pck.get_texto(790116),1,255);
end if;

ds_consistencia_p := ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
