SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE agecons_consiste_cmce (cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp) AS $body$
DECLARE

 
ie_tipo_convenio_w	smallint;
cd_especialidade_w	integer;
ie_fora_vigencia_w	varchar(1) := 'S';
qt_existe_regra_w	bigint;
ie_consiste_contra_ref_w	varchar(1) := 'N';
				

BEGIN 
 
ie_consiste_contra_ref_w := obter_param_usuario(821, 480, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_consiste_contra_ref_w);
 
select	max(ie_tipo_convenio) 
into STRICT	ie_tipo_convenio_w 
from	convenio 
where 	cd_convenio = cd_convenio_p;
 
if (ie_tipo_convenio_w = 3) then 
	select	max(cd_especialidade) 
	into STRICT	cd_especialidade_w 
	from	agenda 
	where	cd_agenda = cd_agenda_p;
	 
	select	count(*) 
	into STRICT	qt_existe_regra_w 
	from	pessoa_fisica_contra_ref 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	 
	if (qt_existe_regra_w > 0) and (ie_consiste_contra_ref_w = 'S') then 
	 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_fora_vigencia_w 
		from	pessoa_fisica_contra_ref 
		where	coalesce(cd_especialidade,cd_especialidade_w) = cd_especialidade_w	 
		and		trunc(dt_agenda_p)	>= coalesce(trunc(dt_inicio), 		trunc(dt_agenda_p)) 
		and		trunc(dt_agenda_p)	<= coalesce(trunc(dt_encerramento), trunc(dt_agenda_p)) 
		--and		((dt_inicio <= dt_agenda_p) and ((dt_agenda_p <= dt_encerramento) or (dt_encerramento is null))) 
		and		coalesce(cd_pessoa_fisica,cd_pessoa_fisica_p) = cd_pessoa_fisica_p 
		and		(nr_cmce IS NOT NULL AND nr_cmce::text <> '');
		 
		if (ie_fora_vigencia_w = 'N') then 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(264107);
		end if;
	 
	end if;	
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
