CREATE OR REPLACE PROCEDURE agecons_obter_pf_lista_email ( ds_lista_pf_p text, ds_lista_email_pf_p INOUT text) AS $body$
DECLARE


tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
ds_lista_Agenda_w	varchar(2000);
ds_lista_agenda_exame_w	varchar(2000);
ds_lista_email_pf_w	varchar(2000);
ie_pos_sep_w		smallint;
ie_pos_virgula_aux_w	smallint;


BEGIN
IF (ds_lista_pf_p IS NOT NULL AND ds_lista_pf_p::text <> '') THEN
	ds_lista_Agenda_w	:= ds_lista_pf_p;

	WHILE	(ds_lista_Agenda_w IS NOT NULL AND ds_lista_Agenda_w::text <> '') LOOP
		BEGIN
		tam_lista_w		:= LENGTH(ds_lista_Agenda_w);
		ie_pos_virgula_w	:= position(',' in ds_lista_Agenda_w);
		ie_pos_sep_w		:= position('-' in ds_lista_Agenda_w);

		IF (ie_pos_sep_w <> 0) THEN
			ie_pos_virgula_aux_w	:= position(',' in SUBSTR(ds_lista_Agenda_w, ie_pos_sep_w + 1, tam_lista_w));
			ds_lista_email_pf_w	:= ds_lista_email_pf_w || SUBSTR(ds_lista_Agenda_w, ie_pos_sep_w + 1, ie_pos_virgula_aux_w);
		END IF;
		ds_lista_Agenda_w	:= SUBSTR(ds_lista_Agenda_w, (ie_pos_virgula_w + 1), tam_lista_w);
		END;
	END LOOP;

END IF;

ds_lista_email_pf_p	:= ds_lista_email_pf_w;

COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
