SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE agecons_restaurar_horario ( cd_agenda_p bigint, nr_seq_agenda_p bigint, cd_motivo_p bigint, ds_motivo_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nm_usuario_acesso_w	varchar(15);
cd_pessoa_fisica_w	varchar(10);
nm_paciente_w		varchar(80);
dt_agenda_w		timestamp;
ds_motivo_agenda_w	varchar(255);
cd_tipo_agenda_w		bigint;
ie_exige_motivo_w		varchar(1) := 'S';


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	
	ie_exige_motivo_w := Obter_Param_Usuario(821, 232, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_exige_motivo_w);

	select	nm_usuario_acesso,
		cd_pessoa_fisica,
		nm_paciente,
		dt_agenda,
		obter_tipo_agenda(cd_agenda)
	into STRICT	nm_usuario_acesso_w,
		cd_pessoa_fisica_w,
		nm_paciente_w,
		dt_agenda_w,
		cd_tipo_agenda_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_p;
	
	if (cd_tipo_agenda_w = 3) then
		ie_exige_motivo_w := Obter_Param_Usuario(821, 232, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_exige_motivo_w);
	end if;
	

	if (coalesce(nm_usuario_acesso_w::text, '') = '') then

		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278082,null);

/*	elsif	(nm_usuario_p <> nm_usuario_acesso_w) then

		ds_erro_p	:= 'Este horario nao pode ser restaurado. Somente o usuario que cadastrou o agendamento pode restaurar o horario';
*/
	elsif (coalesce(cd_motivo_p::text, '') = '') and (coalesce(ds_motivo_p::text, '') = '') and (ie_exige_motivo_w = 'S') then

		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278083,null);

	else

		begin

		/* obter o motivo agenda */

		select	substr(max(ds_motivo),1,255)
		into STRICT	ds_motivo_agenda_w
		from	agenda_motivo
		where	nr_sequencia = cd_motivo_p;

		/* gravar historico */

		CALL gerar_agenda_consulta_hist(cd_agenda_p,
						nr_seq_agenda_p,
						'RES',
						nm_usuario_p,
						substr(WHEB_MENSAGEM_PCK.get_texto(790121,
									'dt_agenda_w='||to_char(dt_agenda_w,'dd/mm/yyyy hh24:mi:ss')||
									';ds_motivo_w='||ds_motivo_p||
									';ds_motivo_agenda_w='||ds_motivo_agenda_w),1,2000),
						cd_pessoa_fisica_w,
						substr(nm_paciente_w,1,60),
						dt_agenda_w);
		
		/* restaurar horario agenda */

		update	agenda_consulta
		set	ie_status_agenda	= 'L',
			nm_usuario_acesso	= ''
		where	nr_sequencia		= nr_seq_agenda_p;

		commit;

		exception
			when others then
			ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(281378,null);
			rollback;
		end;

	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
