CREATE OR REPLACE PROCEDURE agecons_verifica_se_dia_bloq ( cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text, hr_horario_p timestamp, ie_Status_p INOUT text) AS $body$
DECLARE



qt_bloqueio_w		bigint;
ie_dia_semana_w		varchar(10);
ie_Status_w			varchar(4000);
ie_bloqueio_w		varchar(1);
hr_horario_w		timestamp;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '')then

	begin
	hr_horario_w	:= pkg_date_utils.get_time(trunc(clock_timestamp()), to_char(hr_horario_p, 'hh24:mi:ss'), 0);

	select	Obter_Cod_Dia_Semana(dt_agenda_p)
	into STRICT	ie_dia_semana_w
	;

	SELECT 	coalesce(COUNT(*), 0)
	INTO STRICT	qt_bloqueio_w
	FROM	agenda_bloqueio
	WHERE	cd_agenda				= cd_agenda_p
	AND 	dt_agenda_p				between dt_inicial AND fim_dia(dt_final)
	AND		((coalesce(ie_dia_semana,ie_dia_semana_w) = ie_dia_semana_w)
	OR		(ie_dia_semana	= 9 AND ie_dia_semana_w BETWEEN 2 AND 6))
	AND		hr_horario_w	>=	pkg_date_utils.get_time(trunc(clock_timestamp()), to_char(hr_inicio_bloqueio, 'hh24:mi:ss'), 0)
	AND		hr_horario_w	<= 	pkg_date_utils.get_time(trunc(clock_timestamp()), to_char(hr_final_bloqueio, 'hh24:mi:ss'), 0);

	if (qt_bloqueio_w > 0)then
		ie_status_w	:= 'B';
	else
		ie_status_w	:= 'N';
	end if;

	end;

end if;

ie_status_p := ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
