SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_atribui_pessoa_fisica (nr_sequencia_p bigint, ie_tipo_agenda_p bigint, cd_pessoa_fisica_p bigint, nm_usuario_p text) AS $body$
DECLARE

    w_nr_sequencia  agenda_integrada.nr_sequencia%TYPE;

BEGIN
	IF (coalesce(nr_sequencia_p,0) > 0) THEN
		IF ((coalesce(ie_tipo_agenda_p,0) = 3) OR (coalesce(ie_tipo_agenda_p,0) = 5)) THEN
			SELECT  coalesce(max(agin.nr_sequencia),0) nr_sequencia
            INTO STRICT    w_nr_sequencia
            FROM    agenda_consulta agco,
                    agenda_integrada_item aginit,
                    agenda_integrada agin
            WHERE   aginit.nr_seq_agenda_int = agin.nr_sequencia
            AND     agco.nr_sequencia = aginit.nr_seq_agenda_cons
            AND     agco.nr_sequencia = nr_sequencia_p;
		ELSE
			SELECT  coalesce(max(agin.nr_sequencia),0) nr_sequencia
            INTO STRICT    w_nr_sequencia
            FROM    agenda_paciente agpa,
                    agenda_integrada_item aginit,
                    agenda_integrada agin
            WHERE   aginit.nr_seq_agenda_int = agin.nr_sequencia
            AND     agpa.nr_sequencia = aginit.nr_seq_agenda_exame
            AND     agpa.nr_sequencia = nr_sequencia_p;
		END IF;
        BEGIN
            UPDATE  agenda_integrada
			SET     cd_pessoa_fisica = cd_pessoa_fisica_p,
                    dt_atualizacao = clock_timestamp(),
                    nm_usuario = nm_usuario_p
			WHERE   nr_sequencia = w_nr_sequencia
			AND     coalesce(cd_pessoa_fisica::text, '') = '';
        END;
	END IF;

	COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
