CREATE OR REPLACE PROCEDURE ageint_att_classif_item_ret (ie_classif_agenda_p text, ie_classif_ret_agenda_p text, nr_minuto_duracao_p bigint, nr_seq_ageint_item_p bigint, cd_estabelecimento_p bigint, ie_marcando_p text) AS $body$
DECLARE


nr_duracao_w bigint;


BEGIN

if (ie_marcando_p <> 'S') then

	update agenda_integrada_item
	set    ie_classif_agenda = ie_classif_agenda_p,
	       nr_minuto_duracao = nr_minuto_duracao_p
	where  nr_sequencia = nr_seq_ageint_item_p;
	
elsif (ie_classif_ret_agenda_p IS NOT NULL AND ie_classif_ret_agenda_p::text <> '') and (nr_seq_ageint_item_p > 0) then

	nr_duracao_w := ageint_obter_dur_classif(ie_classif_ret_agenda_p, cd_estabelecimento_p);	

	update agenda_integrada_item
	set    ie_classif_agenda = ie_classif_ret_agenda_p,
	       nr_minuto_duracao = CASE WHEN coalesce(nr_duracao_w, 0)=0 THEN  nr_minuto_duracao  ELSE nr_duracao_w END
	where  nr_sequencia = nr_seq_ageint_item_p;
	
end if;

commit;

end;
$body$
