CREATE OR REPLACE PROCEDURE ageint_atualizar_classif_item (ie_classif_agenda_p text, nr_seq_ageint_item_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_duracao_w	bigint					;


BEGIN

if (ie_classif_agenda_p IS NOT NULL AND ie_classif_agenda_p::text <> '') and (nr_seq_ageint_item_p > 0) then

	nr_duracao_w	:= Ageint_Obter_Dur_Classif(ie_classif_agenda_p, cd_estabelecimento_p);

	update	agenda_integrada_item
	set		ie_classif_agenda	= ie_classif_agenda_p,
			nr_minuto_duracao	= CASE WHEN coalesce(nr_duracao_w,0)=0 THEN  nr_minuto_duracao  ELSE nr_duracao_w END
	where	nr_sequencia		= nr_seq_ageint_item_p;

	commit;
end if;

end;
$body$
