SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_atualiza_conv_quimio (nr_seq_pend_agenda_p bigint, nr_seq_ageint_p bigint) AS $body$
DECLARE


nr_seq_paciente_w 		bigint;
cd_convenio_w 			integer;
cd_categoria_w 			varchar(10);
cd_plano_w 				varchar(10);
ie_tipo_Atendimento_w 	smallint;
cd_usuario_convenio_w 	varchar(30);
nr_doc_convenio_w 		varchar(20);
ie_possui_pac_set_conv_w  varchar(1);
nm_usuario_w			varchar(15) := obter_usuario_ativo;


BEGIN
	if (nr_seq_pend_agenda_p <> 0) then
		
		select  max(nr_seq_paciente)
		into STRICT 	nr_seq_paciente_w
		from paciente_atendimento
		where nr_seq_pend_Agenda = nr_seq_pend_Agenda_p;
		
		select	max(cd_convenio),
				max(cd_categoria),
				max(cd_plano),
				max(ie_tipo_Atendimento),
				max(cd_usuario_convenio),
				max(nr_doc_convenio)
		into STRICT 	cd_convenio_w,
				cd_categoria_w,
				cd_plano_w,
				ie_tipo_Atendimento_w,
				cd_usuario_convenio_w,
				nr_doc_convenio_w
		from agenda_integrada
		where nr_sequencia = nr_seq_ageint_p;
		
		if (nr_seq_paciente_w <> 0) and (cd_convenio_w <> 0)then
			
			select 	coalesce(max('S'),'N')
			into STRICT	ie_possui_pac_set_conv_w
			from 	paciente_setor_convenio
			where 	nr_seq_paciente = nr_seq_paciente_w;
			
			if (ie_possui_pac_set_conv_w = 'N') then
				insert into paciente_setor_convenio(nr_sequencia,
					dt_atualizacao, 
					nm_usuario,  
					dt_atualizacao_nrec, 
					nm_usuario_nrec, 
					cd_convenio,
					cd_categoria, 
					cd_plano, 
					cd_usuario_convenio, 
					nr_seq_paciente, 
					ie_tipo_atendimento, 
					nr_doc_convenio)
				values (nextval('paciente_setor_convenio_seq'),
					clock_timestamp(),
					nm_usuario_w,
					clock_timestamp(),
					nm_usuario_w,
					cd_convenio_w,
					cd_categoria_w,
					cd_plano_w,
					cd_usuario_convenio_w,
					nr_seq_paciente_w,
					ie_tipo_Atendimento_w,
					nr_doc_convenio_w);
			
			else
				update 	paciente_setor_convenio
				set	cd_convenio = cd_convenio_w,
					cd_categoria = cd_categoria_w,
					cd_plano     = cd_plano_w,
					ie_tipo_atendimento = ie_tipo_Atendimento_w,
					cd_usuario_convenio= cd_usuario_convenio_w,
					nr_doc_convenio = nr_doc_convenio_w,
					nm_usuario	= nm_usuario_w,
					dt_atualizacao 	= clock_timestamp()
				where 	nr_seq_paciente = nr_seq_paciente_w;
			end if;
			commit;
			CALL AGEINT_GERAR_AUTOR_QUIMIO(nr_seq_ageint_p, 'Q', nm_usuario_w);
		end if;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
