CREATE OR REPLACE PROCEDURE ageint_atualiza_dur_hor_sug ( nr_seq_ageint_p bigint, nr_min_duracao_p bigint, ie_transferencia_p text, nm_usuario_p text) AS $body$
DECLARE

										 
nr_seq_hor_sugerido_w	bigint;
dt_agenda_sugerida_w	timestamp;
dt_agenda_atualizada_w	timestamp;
												
C01 CURSOR FOR 
	SELECT a.nr_sequencia, 
			a.hr_agenda 
	FROM agenda_integrada_item d, agenda c, ageint_lib_usuario b, ageint_sugestao_horarios a
LEFT OUTER JOIN pessoa_fisica e ON (a.cd_pessoa_fisica = e.cd_pessoa_Fisica)
WHERE a.nr_seq_ageint_lib = b.nr_sequencia and b.nr_seq_ageint   = nr_seq_ageint_p and a.cd_agenda     = c.cd_agenda and a.nr_seq_item    = d.nr_sequencia  and ((coalesce(d.nr_seq_agenda_exame::text, '') = '' 
	and 	coalesce(d.nr_seq_agenda_cons::text, '') = '' 
	and 	coalesce(d.nr_seq_agequi::text, '') = '') or (ie_transferencia_p = 'S')) and d.nr_sequencia		> (	SELECT	min(x.nr_sequencia) 
									from	agenda_integrada_item x 
									where	x.nr_seq_agenda_int = nr_seq_ageint_p) order by 1;
												

BEGIN 
 
if (nr_seq_ageint_p IS NOT NULL AND nr_seq_ageint_p::text <> '') and (nr_min_duracao_p IS NOT NULL AND nr_min_duracao_p::text <> '')then 
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_hor_sugerido_w, 
		dt_agenda_sugerida_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		if (nr_seq_hor_sugerido_w IS NOT NULL AND nr_seq_hor_sugerido_w::text <> '') and (nr_min_duracao_p IS NOT NULL AND nr_min_duracao_p::text <> '')then			 
			dt_agenda_atualizada_w	:=	dt_agenda_sugerida_w + nr_min_duracao_p / 1440;				
		end if;
		 
		update	ageint_sugestao_horarios 
		set		hr_agenda		=	dt_agenda_atualizada_w, 
				nm_usuario		= 	nm_usuario_p 
		where	nr_sequencia	= 	nr_seq_hor_sugerido_w;
		 
		commit;
		 
		end;
	end loop;
	close C01;
 
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
