CREATE OR REPLACE PROCEDURE ageint_atualiza_mult_ag_pac (NM_PESSOA_FISICA_P text, NR_TELEFONE_P text, DT_NASCIMENTO_PAC_P timestamp, CD_PESSOA_FISICA_P text, NR_SEQ_AGENDA_P bigint, CD_TIPO_AGENDA_P bigint, NM_USUARIO_P text) AS $body$
BEGIN
	
	IF ((CD_TIPO_AGENDA_P = 3) OR (CD_TIPO_AGENDA_P = 5)) THEN
	
		UPDATE AGENDA_CONSULTA
			SET NM_PACIENTE = NM_PESSOA_FISICA_P,
						  NR_TELEFONE = NR_TELEFONE_P,
						  DT_NASCIMENTO_PAC = DT_NASCIMENTO_PAC_P,
						  CD_PESSOA_FISICA = CD_PESSOA_FISICA_P,
						  NM_USUARIO = NM_USUARIO_P,
						  DT_ATUALIZACAO = clock_timestamp()
		WHERE NR_SEQUENCIA = NR_SEQ_AGENDA_P;
	
	ELSE
	
		UPDATE AGENDA_PACIENTE 
			SET NM_PACIENTE = NM_PESSOA_FISICA_P,
						  NR_TELEFONE = NR_TELEFONE_P,
						  DT_NASCIMENTO_PAC = DT_NASCIMENTO_PAC_P,
						  CD_PESSOA_FISICA = CD_PESSOA_FISICA_P,
						  NM_USUARIO = NM_USUARIO_P,
						  DT_ATUALIZACAO = clock_timestamp()
		WHERE NR_SEQUENCIA = NR_SEQ_AGENDA_P;
	
	END IF;

COMMIT;
	
END;
$body$
