CREATE OR REPLACE PROCEDURE ageint_cancelar_pendencia (nr_seq_pend_agenda_p bigint, nr_seq_agenda_quimio_p bigint, ie_opcao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_motivo_p text default null, ds_motivo_p text default null) AS $body$
DECLARE

 
nr_seq_agenda_w		bigint;				
				   
C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	agenda_quimio a, 
		agenda_integrada_item b 
	where	a.nr_seq_pend_agenda = b.nr_seq_pend_quimio 
	and	b.nr_seq_pend_quimio = nr_seq_pend_agenda_p 
	and	a.ie_status_agenda <> 'C' 
	order by 1;				
				   

BEGIN 
/* opção 
  0 - Somente data selecionada 
  1 - Todos do ciclo 
*/
 
 
if (ie_opcao_p = 0) then 
	 
	CALL qt_alterar_status_agenda(nr_seq_agenda_quimio_p, 
				'C', 
				nm_usuario_p, 
				'N', 
				nr_seq_motivo_p, 
				ds_motivo_p, 
				cd_estabelecimento_p, 
				'');
	 
elsif (ie_opcao_p = 1) then		 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL qt_alterar_status_agenda(nr_seq_agenda_w, 
				'C', 
				nm_usuario_p, 
				'N', 
				nr_seq_motivo_p, 
				ds_motivo_p, 
				cd_estabelecimento_p, 
				'');
		end;
	end loop;
	close C01;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
