CREATE OR REPLACE PROCEDURE ageint_confirm_horario_sms ( nr_sequencia_p bigint, nr_seq_forma_p bigint, nm_usuario_p text) AS $body$
DECLARE


						
nr_seq_agenda_exame_w	agenda_integrada_item.nr_seq_agenda_exame%type;
nr_seq_agenda_cons_w	agenda_integrada_item.nr_seq_agenda_cons%type;
C01 CURSOR FOR
	SELECT	nr_seq_agenda_exame,
		nr_seq_agenda_cons
	from	agenda_integrada_item
	where	nr_seq_agenda_int =  nr_sequencia_p;
	

BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	open C01;
	loop
	fetch C01 into	
		nr_seq_agenda_exame_w,
		nr_seq_agenda_cons_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (coalesce(nr_seq_agenda_cons_w,0) > 0) then
		
			if (coalesce(nr_seq_forma_p,0) > 0) then
			
				update	agenda_consulta
				set	dt_confirmacao 			= clock_timestamp(),
					nm_usuario_confirm 		= nm_usuario_p,
					nr_seq_forma_confirmacao	= nr_seq_forma_p
				where	nr_sequencia			= nr_seq_agenda_cons_w;
			
			else
			
				update	agenda_consulta
				set	dt_confirmacao 			= clock_timestamp(),
					nm_usuario_confirm 		= nm_usuario_p
				where	nr_sequencia 			= nr_seq_agenda_cons_w;
							
			end if;
			
		elsif (coalesce(nr_seq_agenda_exame_w,0) > 0) then
		
			if (coalesce(nr_seq_forma_p,0) > 0) then
			
				update	agenda_paciente
				set	dt_confirmacao 			= clock_timestamp(),
					nm_usuario_confirm 		= nm_usuario_p,
					nr_seq_forma_confirmacao	= nr_seq_forma_p
				where	nr_sequencia			= nr_seq_agenda_exame_w;
			
			else
			
				update	agenda_paciente
				set	dt_confirmacao 			= clock_timestamp(),
					nm_usuario_confirm 		= nm_usuario_p
				where	nr_sequencia 			= nr_seq_agenda_exame_w;
				
			end if;
		end if;
		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
