SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_consiste_exame_adic ( cd_agenda_p bigint, nr_seq_item_p bigint, ie_tipo_agendamento_p text, cd_convenio_p bigint, cd_categoria_p text, cd_estabelecimento_p bigint, cd_plano_p text, cd_profissional_p text, ds_exames_p INOUT text, dt_agenda_p timestamp default null, ie_dia_da_Semana_p text default null) AS $body$
DECLARE


qt_regra_w		bigint;
nr_seq_proc_interno_w	bigint;
ds_exame_w		varchar(100);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_area_proced_w	integer;
cd_espec_proced_w	integer;
cd_grupo_proced_w	bigint;
nr_seq_regra_w		bigint;
qt_exclusivo_w		bigint;
nr_seq_regra_final_w	bigint;
ie_permite_w		varchar(1);
nr_seq_grupo_w		bigint;
ds_exame_ww		varchar(255);
ds_exame_grupo_w	varchar(255);
nr_seq_grupo_selec_w	bigint;
ie_tipo_convenio_w		smallint;
ie_dia_da_Semana_w		agenda_regra.ie_dia_semana%type;
			
C01 CURSOR FOR
	SELECT	nr_seq_proc_interno,
		nr_seq_grupo_selec
	from	ageint_exame_adic_item
	where	nr_seq_item	= nr_seq_item_p
	order by 1;
	
	
C02 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_regra
	where	coalesce(cd_estabelecimento, cd_Estabelecimento_p) = cd_estabelecimento_p
	and	cd_agenda = cd_agenda_p
	and	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
	and	((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
	and	((cd_plano_convenio = cd_plano_p) or (coalesce(cd_plano_convenio::text, '') = ''))
	and	((cd_area_proc = cd_area_proced_w) or (coalesce(cd_area_proc::text, '') = ''))
	and	((cd_especialidade = cd_espec_proced_w) or (coalesce(cd_especialidade::text, '') = ''))
	and	((cd_grupo_proc = cd_grupo_proced_w) or (coalesce(cd_grupo_proc::text, '') = ''))
	and	((cd_procedimento = cd_procedimento_w) or (coalesce(cd_procedimento::text, '') = ''))
	and	((coalesce(cd_procedimento::text, '') = '') or ((ie_origem_proced = ie_origem_proced_w) or (coalesce(ie_origem_proced::text, '') = '')))
	and	((nr_seq_proc_interno = nr_seq_proc_interno_w) or (coalesce(nr_seq_proc_interno::text, '') = ''))
	and	((cd_medico = cd_profissional_p) or (coalesce(cd_medico::text, '') = ''))
	and	((ie_tipo_convenio = ie_tipo_convenio_w) or (coalesce(ie_tipo_convenio::text, '') = ''))
	and	ie_permite in ('S','N')
	and	coalesce(nr_seq_grupo_ageint::text, '') = ''
	and	coalesce(ie_situacao,'A') = 'A'
	and	coalesce(dt_agenda_p,clock_timestamp()) between coalesce(dt_inicio_vigencia,to_date('01/01/1900 00:00:01','dd/mm/yyyy hh24:mi:ss')) and coalesce(dt_fim_vigencia,to_date('01/01/2099 23:59:59','dd/mm/yyyy hh24:mi:ss'))
	and	((coalesce(ie_dia_semana,ie_dia_da_Semana_w) = ie_dia_da_Semana_w) or ((coalesce(ie_dia_semana,ie_dia_da_Semana_w) = 9) and (ie_dia_da_Semana_w not in (7,1))))
	order by coalesce(cd_procedimento,0),
		coalesce(nr_seq_proc_interno,0),
		coalesce(cd_area_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_convenio, 0),
		coalesce(cd_medico, 0),
		coalesce(ie_permite,'S');
		
		
C03 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_cons_regra_proc
	where	coalesce(cd_estabelecimento, cd_Estabelecimento_p) = cd_estabelecimento_p
	and	coalesce(cd_agenda,cd_agenda_p) 		= cd_agenda_p
	and	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
	and	((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
	and	((cd_plano_convenio = cd_plano_p) or (coalesce(cd_plano_convenio::text, '') = ''))
	and	((cd_area_procedimento = cd_area_proced_w) or (coalesce(cd_area_procedimento::text, '') = ''))
	and	((cd_especialidade = cd_espec_proced_w) or (coalesce(cd_especialidade::text, '') = ''))
	and	((cd_grupo_proc = cd_grupo_proced_w) or (coalesce(cd_grupo_proc::text, '') = ''))
	and	((cd_procedimento = cd_procedimento_w) or (coalesce(cd_procedimento::text, '') = ''))
	and	((coalesce(cd_procedimento::text, '') = '') or ((ie_origem_proced = ie_origem_proced_w) or (coalesce(ie_origem_proced::text, '') = '')))
	and	((nr_seq_proc_interno = nr_seq_proc_interno_w) or (coalesce(nr_seq_proc_interno::text, '') = ''))
	and	ie_permite in ('S','N')
	and ((ie_agenda = ie_tipo_agendamento_p) or (coalesce(ie_agenda,'A') = 'A'))
	order by coalesce(cd_procedimento,0),
		coalesce(nr_seq_proc_interno,0),
		coalesce(cd_area_procedimento,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_convenio, 0);
		
C04 CURSOR FOR
	SELECT	a.nr_sequencia
	from	agenda_int_grupo a,
		agenda_int_grupo_item b
	where	a.nr_sequencia = b.nr_seq_grupo
	and	a.ie_situacao  = 'A'
	and	ie_permite_w	<> 'S'
	and	b.nr_seq_proc_interno =	nr_seq_proc_interno_w;
	
C05 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_regra
	where	coalesce(cd_estabelecimento, cd_Estabelecimento_p) = cd_estabelecimento_p
	and	cd_agenda = cd_agenda_p
	and	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
	and	((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
	and	((cd_plano_convenio = cd_plano_p) or (coalesce(cd_plano_convenio::text, '') = ''))
	and	nr_seq_grupo_ageint	= nr_seq_grupo_selec_w
	and	((cd_medico = cd_profissional_p) or (coalesce(cd_medico::text, '') = ''))
	and	ie_permite in ('S','N')
	and	ie_permite_w	<> 'S'
	and	coalesce(dt_agenda_p,clock_timestamp()) between coalesce(dt_inicio_vigencia,to_date('01/01/1900 00:00:01','dd/mm/yyyy hh24:mi:ss')) and coalesce(dt_fim_vigencia,to_date('01/01/2099 23:59:59','dd/mm/yyyy hh24:mi:ss'))
	and	((coalesce(ie_dia_semana,ie_dia_da_Semana_w) = ie_dia_da_Semana_w) or ((coalesce(ie_dia_semana,ie_dia_da_Semana_w) = 9) and (ie_dia_da_Semana_w not in (7,1))))
	order by coalesce(cd_procedimento,0),
		coalesce(nr_seq_proc_interno,0),
		coalesce(cd_area_proc,0),
		coalesce(cd_especialidade,0),
		coalesce(cd_grupo_proc,0),
		coalesce(cd_convenio, 0),
		coalesce(cd_medico, 0),
		coalesce(ie_permite,'S');
			

BEGIN
ie_dia_da_Semana_w := ie_dia_da_Semana_p;
if (coalesce(ie_dia_da_Semana_p::text, '') = '') then
	ie_dia_da_Semana_w := Obter_Cod_Dia_Semana(coalesce(dt_agenda_p,clock_timestamp()));
end if;

open C01;
loop
fetch C01 into	
	nr_seq_proc_interno_w,
	nr_seq_grupo_selec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_exame_ww	:= '';
	ie_permite_w	:= 'N';	
	SELECT * FROM obter_proc_tab_interno_conv(
				nr_seq_proc_interno_w, cd_estabelecimento_p, cd_convenio_p, cd_categoria_p, cd_plano_p, null, cd_procedimento_w, ie_origem_proced_w, null, clock_timestamp(), null, null, null, null, null, null, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;

	select	coalesce(max(cd_area_procedimento),0),
		coalesce(max(cd_especialidade),0),
		coalesce(max(cd_grupo_proc),0)
	into STRICT	cd_area_proced_w,
		cd_espec_proced_w,
		cd_grupo_proced_w
	from	estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w;
	
	if (ie_tipo_agendamento_p	= 'C') then
		select	count(*)
		into STRICT	qt_exclusivo_w
		from	agenda_cons_regra_proc
		where	coalesce(cd_estabelecimento, cd_Estabelecimento_p) = cd_estabelecimento_p
		and	coalesce(cd_agenda,cd_agenda_p) 		= cd_agenda_p
		and	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
		and	coalesce(ie_permite,'S') = 'E';
		
		if (qt_exclusivo_w > 0) then

			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_regra_w
			from	agenda_cons_regra_proc
			where	coalesce(cd_estabelecimento, cd_Estabelecimento_p) = cd_estabelecimento_p
			and	coalesce(cd_agenda,cd_agenda_p) 		= cd_agenda_p
			and	((cd_convenio 		= cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
			and	((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
			and	((cd_plano_convenio = cd_plano_p) or (coalesce(cd_plano_convenio::text, '') = ''))
			and	((cd_area_procedimento = cd_area_proced_w) or (coalesce(cd_area_procedimento::text, '') = ''))
			and	((cd_especialidade = cd_espec_proced_w) or (coalesce(cd_especialidade::text, '') = ''))
			and	((cd_grupo_proc = cd_grupo_proced_w) or (coalesce(cd_grupo_proc::text, '') = ''))
			and	((cd_procedimento = cd_procedimento_w) or (coalesce(cd_procedimento::text, '') = ''))
			and	((coalesce(cd_procedimento::text, '') = '') or ((ie_origem_proced = ie_origem_proced_w) or (coalesce(ie_origem_proced::text, '') = '')))
			and	((nr_seq_proc_interno = nr_seq_proc_interno_w) or (coalesce(nr_seq_proc_interno::text, '') = ''))
			and	coalesce(ie_permite,'S') = 'E'
			and ((ie_agenda = ie_tipo_agendamento_p) or (coalesce(ie_agenda,'A') = 'A'));
				
			if (qt_regra_w	= 0) then
				select	substr(ds_proc_exame,1,100)
				into STRICT	ds_exame_w
				from	proc_interno
				where	nr_sequencia	= nr_seq_proc_interno_w;
				
				ds_exames_p	:= ds_exames_p || ds_exame_w || ', ';
			end if;
		else
			nr_seq_regra_final_w := 0;
			
			open C03;
			loop
			fetch C03 into	
				nr_seq_regra_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin
				nr_seq_regra_final_w	:= nr_seq_regra_w;
				end;
			end loop;
			close C03;
			
			select	coalesce(max(ie_permite),'N')	
			into STRICT	ie_permite_w
			from	agenda_cons_regra_proc
			where	nr_sequencia	= nr_seq_regra_final_w;
			
			if (ie_permite_w	= 'N') then
				select	substr(ds_proc_exame,1,100)
				into STRICT	ds_exame_w
				from	proc_interno
				where	nr_sequencia	= nr_seq_proc_interno_w;
			
				ds_exames_p	:= ds_exames_p || ds_exame_w || ', ';
			end if;
		end if;
		
	else
		select	count(*)
		into STRICT	qt_exclusivo_w
		from	agenda_regra
		where	coalesce(cd_estabelecimento, cd_Estabelecimento_p) = cd_estabelecimento_p
		and	cd_agenda 		= cd_agenda_p
		and	((cd_convenio = cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
		and	((cd_medico = cd_profissional_p) or (coalesce(cd_medico::text, '') = ''))
		and	ie_permite = 'E';
		
		if (qt_exclusivo_w > 0) then

			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_regra_w
			from	agenda_regra
			where	coalesce(cd_estabelecimento, cd_Estabelecimento_p) = cd_estabelecimento_p
			and	cd_agenda 		= cd_agenda_p
			and	((cd_convenio 		= cd_convenio_p) or (coalesce(cd_convenio::text, '') = ''))
			and	((cd_categoria = cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
			and	((cd_plano_convenio = cd_plano_p) or (coalesce(cd_plano_convenio::text, '') = ''))
			and	((cd_area_proc = cd_area_proced_w) or (coalesce(cd_area_proc::text, '') = ''))
			and	((cd_especialidade = cd_espec_proced_w) or (coalesce(cd_especialidade::text, '') = ''))
			and	((cd_grupo_proc = cd_grupo_proced_w) or (coalesce(cd_grupo_proc::text, '') = ''))
			and	((cd_procedimento = cd_procedimento_w) or (coalesce(cd_procedimento::text, '') = ''))
			and	((coalesce(cd_procedimento::text, '') = '') or ((ie_origem_proced = ie_origem_proced_w) or (coalesce(ie_origem_proced::text, '') = '')))
			and	((nr_seq_proc_interno = nr_seq_proc_interno_w) or (coalesce(nr_seq_proc_interno::text, '') = ''))
			and	((cd_medico 		= cd_profissional_p) or (coalesce(cd_medico::text, '') = ''))
			and	ie_permite = 'E';
				
			if (qt_regra_w	= 0) then
				select	substr(ds_proc_exame,1,100)
				into STRICT	ds_exame_w
				from	proc_interno
				where	nr_sequencia	= nr_seq_proc_interno_w;
				
				ds_exames_p	:= ds_exames_p || ds_exame_w || ', ';
			end if;
		else			
			nr_seq_regra_final_w := 0;
			
			begin
				select	ie_tipo_convenio
				into STRICT 	ie_tipo_convenio_w
				from 	convenio
				where 	cd_convenio = cd_convenio_p;
			EXCEPTION WHEN OTHERS THEN
				ie_tipo_convenio_w := null;
			end;
			
			open C02;
			loop
			fetch C02 into	
				nr_seq_regra_w;
			EXIT WHEN NOT FOUND; /* apply on C02 */
				begin
				nr_seq_regra_final_w	:= nr_seq_regra_w;
				end;
			end loop;
			close C02;
			
			select	coalesce(max(ie_permite),'N')	
			into STRICT	ie_permite_w
			from	agenda_regra
			where	nr_sequencia	= nr_seq_regra_final_w;
			
			if (ie_permite_w	= 'N') then
				select	substr(ds_proc_exame,1,100)
				into STRICT	ds_exame_w
				from	proc_interno
				where	nr_sequencia	= nr_seq_proc_interno_w;
			
				ds_exame_ww	:= ds_exame_ww || ds_exame_w || ', ';

			end if;
						
			if (ie_permite_w	<> 'S') then
				/*open C04;
				loop
				fetch C04 into	
					nr_seq_grupo_w;
				exit when C04%notfound;
					begin
					open C05;
					loop
					fetch C05 into	
						nr_seq_regra_w;
					exit when C05%notfound;
						begin
						if	(ie_permite_w	<> 'S') then
							select	nvl(max(ie_permite),'N')	
							into	ie_permite_w
							from	agenda_regra
							where	nr_sequencia	= nr_seq_regra_w;
						end if;
						end;
					end loop;
					close C05;
					end;
				end loop; 
				close C04;*/
				open C05;
				loop
				fetch C05 into	
					nr_seq_regra_w;
				EXIT WHEN NOT FOUND; /* apply on C05 */
					begin
					if (ie_permite_w	<> 'S') then
						select	coalesce(max(ie_permite),'N')	
						into STRICT	ie_permite_w
						from	agenda_regra
						where	nr_sequencia	= nr_seq_regra_w;
					end if;
					end;
				end loop;
				close C05;
			end if;
			if (ie_permite_w	= 'S') then
				ds_exame_ww	:= '';
			end if;
			if (ds_exame_ww IS NOT NULL AND ds_exame_ww::text <> '') then
				ds_exames_p	:= ds_exames_p || ', '|| ds_exame_ww;
			end if;
		end if;	
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ageint_consiste_exame_adic ( cd_agenda_p bigint, nr_seq_item_p bigint, ie_tipo_agendamento_p text, cd_convenio_p bigint, cd_categoria_p text, cd_estabelecimento_p bigint, cd_plano_p text, cd_profissional_p text, ds_exames_p INOUT text, dt_agenda_p timestamp default null, ie_dia_da_Semana_p text default null) FROM PUBLIC;

