CREATE OR REPLACE PROCEDURE ageint_consiste_req_exames (nr_seq_ageint_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_proc_interno_w	bigint;
ds_erro_w		varchar(255) := '';
ds_proc_interno_w	varchar(100);
cd_medico_req_w		varchar(10);
ie_exige_req_ex_w	varchar(1);
ie_exige_req_se_w	varchar(1);
ie_tipo_agendamento_w	varchar(15);

C01 CURSOR FOR
	SELECT	nr_Seq_proc_interno,
		substr(obter_desc_proc_interno(nr_seq_proc_interno),1,100),
		cd_medico_req,
		ie_tipo_agendamento
	from	agenda_integrada_item
	where	nr_seq_agenda_int	= nr_seq_ageint_p
	and	ie_tipo_agendamento	in ('E','S')
	order by nr_seq_proc_interno;


BEGIN

ie_exige_req_ex_w := Obter_Param_Usuario(869, 240, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_exige_req_ex_w);
ie_exige_req_se_w := Obter_Param_Usuario(869, 347, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_exige_req_se_w);

if	((ie_exige_req_ex_w = 'S') or (ie_exige_req_se_w = 'S')) then
	open C01;
	loop
	fetch C01 into
		nr_seq_proc_interno_w,
		ds_proc_interno_w,
		cd_medico_req_w,
		ie_tipo_agendamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(cd_medico_req_w::text, '') = '') and
			((ie_exige_req_ex_w = 'S' AND ie_tipo_agendamento_w = 'E') or
			 (ie_exige_req_se_w = 'S' AND ie_tipo_agendamento_w = 'S')) then
			ds_erro_w	:= substr(ds_erro_w || nr_seq_proc_interno_w || ' - ' || ds_proc_interno_w || ', ',1,255);
		end if;
		end;
	end loop;
	close C01;
end if;

ds_erro_p	:= substr(ds_erro_w, 1, length(ds_Erro_w) - 2);

end;
