CREATE OR REPLACE PROCEDURE ageint_consistir_campo_obrig ( nr_seq_ageint_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

ie_peso_w		varchar(1);
ie_altura_w		varchar(1);
ie_medico_solic_w	varchar(1);
ie_telefone_w		varchar(1);

qt_consistencia_w	bigint;

qt_altura_cm_w		real;
qt_peso_w		real;
nr_telefone_w		varchar(60);
cd_medico_solicitante_w	varchar(10);

ds_campos_w		varchar(255);

ds_enter_w		varchar(10)	:=  chr(13) || chr(10);


BEGIN

ds_erro_p	:= '';
ds_campos_w	:= '';

select	coalesce(max(ie_peso),'N'),
	coalesce(max(ie_altura),'N'),
	coalesce(max(ie_medico_solic),'N'),
	coalesce(max(ie_telefone),'N')
into STRICT	ie_peso_w,
	ie_altura_w,
	ie_medico_solic_w,
	ie_telefone_w
from	ageint_campo_obrig_confirm
where	cd_estabelecimento	= cd_estabelecimento_p;

select	max(qt_peso),
	max(qt_altura_cm),
	max(cd_medico_solicitante),
	max(nr_telefone),
  count(1)
into STRICT	qt_peso_w,
	qt_altura_cm_w,
	cd_medico_solicitante_w,
	nr_telefone_w,
  qt_consistencia_w
from	agenda_integrada
where	nr_sequencia 	= nr_seq_ageint_p;

if (not((ie_peso_w = 'S' and coalesce(qt_peso_w::text, '') = '') or (ie_altura_w = 'S' and coalesce(qt_altura_cm_w::text, '') = '') or (ie_medico_solic_w = 'S' and coalesce(cd_medico_solicitante_w::text, '') = '') or (ie_telefone_w = 'S' and coalesce(nr_telefone_w::text, '') = ''))) then

    qt_consistencia_w := 0;

end if;

if (ie_peso_w	= 'S' and coalesce(qt_peso_w::text, '') = '') then
	ds_campos_w	:= ds_campos_w || WHEB_MENSAGEM_PCK.get_texto(279596,null);
	if (substr(ds_campos_w, length(ds_campos_w), 1) = ',')then
	 ds_campos_w	:= ds_campos_w || ' ';
	end if;
end if;

if (ie_altura_w	= 'S' and coalesce(qt_altura_cm_w::text, '') = '') then
	ds_campos_w	:= ds_campos_w || WHEB_MENSAGEM_PCK.get_texto(279595,null);
	if (substr(ds_campos_w, length(ds_campos_w), 1) = ',')then
	 ds_campos_w	:= ds_campos_w || ' ';
	end if;
end if;

if (ie_medico_solic_w	= 'S' and coalesce(cd_medico_solicitante_w::text, '') = '') then
	ds_campos_w	:= ds_campos_w || WHEB_MENSAGEM_PCK.get_texto(279597,null);
	if (substr(ds_campos_w, length(ds_campos_w), 1) = ',')then
	 ds_campos_w	:= ds_campos_w || ' ';
	end if;
end if;

if (ie_telefone_w	= 'S' and coalesce(nr_telefone_w::text, '') = '') then
	ds_campos_w	:= ds_campos_w || WHEB_MENSAGEM_PCK.get_texto(279598,null);
	if (substr(ds_campos_w, length(ds_campos_w), 1) = ',')then
	 ds_campos_w	:= ds_campos_w || ' ';
	end if;
end if;

if (qt_consistencia_w > 0) then
	ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(279599,null)|| ds_enter_w || substr(ds_campos_w,1,length(ds_campos_w)-2);

end if;

end;
$body$
