CREATE OR REPLACE PROCEDURE ageint_consistir_dias_autor (dt_agenda_p timestamp, nr_seq_ageint_item_p bigint, nm_usuario_p text, cd_establecimento_p bigint, ds_consistencia_p INOUT text) AS $body$
DECLARE


nr_seq_regra_w	bigint;
qt_dias_autorizacao_w	smallint;
ie_valida_dias_autor_w	varchar(1);

BEGIN

select	max(nr_seq_regra)
into STRICT	nr_seq_regra_w
from	agenda_integrada_item
where	nr_sequencia = nr_seq_ageint_item_p;


if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	ie_valida_dias_autor_w := obter_param_usuario(869, 435, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_valida_dias_autor_w);

	select	max(qt_dias_autorizacao)
	into STRICT	qt_dias_autorizacao_w
	from	regra_convenio_plano
	where	nr_sequencia = nr_seq_regra_w;

	if (qt_dias_autorizacao_w IS NOT NULL AND qt_dias_autorizacao_w::text <> '') and (dt_agenda_p < trunc(clock_timestamp() + qt_dias_autorizacao_w)) then
		if (coalesce(ie_valida_dias_autor_w,'S')	= 'S') then
			ds_consistencia_p := substr(obter_texto_dic_objeto(223437,wheb_usuario_pck.get_nr_seq_idioma,'NR_DIAS_AUTOR=' ||qt_dias_autorizacao_w|| ';'),1,255);
		else
			ds_consistencia_p := substr(obter_texto_dic_objeto(1056240,wheb_usuario_pck.get_nr_seq_idioma,'NR_DIAS_AUTOR=' ||qt_dias_autorizacao_w|| ';'),1,255);
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
