CREATE OR REPLACE PROCEDURE ageint_consistir_marcacao ( nr_Seq_ageint_p bigint, nr_seq_ageint_item_p bigint, cd_estab_marcacao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text, ie_bloqueia_p INOUT text) AS $body$
DECLARE

	 
qt_outro_estab_w	bigint;
ie_glosa_w		varchar(1);
	

BEGIN 
 
select	count(*) 
into STRICT	qt_outro_estab_w 
from	agenda b, 
		ageint_marcacao_usuario a 
where	a.cd_agenda				= b.cd_agenda 
and		a.nr_seq_ageint			= nr_seq_ageint_p 
and		b.cd_estabelecimento	<> cd_estabelecimento_p;
 
select	max(ie_glosa) 
into STRICT	ie_glosa_w 
from	agenda_integrada_item 
where	nr_sequencia = nr_seq_ageint_item_p;
 
if (ie_glosa_w = 'T') then 
	ds_mensagem_p := wheb_mensagem_pck.get_texto(790835);
	ie_bloqueia_p	:= 'S';
elsif (qt_outro_estab_w	> 0) then 
	ds_mensagem_p := wheb_mensagem_pck.get_texto(790836);
	ie_bloqueia_p	:= 'N';
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
