CREATE OR REPLACE PROCEDURE ageint_consistir_pendencia (nr_seq_motivo_pend_p bigint, ds_observacao_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_obriga_obs_w		varchar(1);


BEGIN

select	coalesce(max(ie_obriga_obs),'N')
into STRICT	ie_obriga_obs_w
from	ageint_motivo_pendencia
where	nr_sequencia = nr_seq_motivo_pend_p;

if (ie_obriga_obs_w = 'S') and (coalesce(ds_observacao_p::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(261525);
end if;

end;
