SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_consitir_ex_agenda ( nr_seq_Ageint_item_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_ageint_p bigint, cd_agenda_p bigint, ie_sugestao_exames_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

 
ds_agenda_w	varchar(50);
ie_tipo_agendamento_w	varchar(3);
nr_seq_regra_w	bigint;
cd_agenda_marc_w	bigint;
ds_retorno_w	varchar(255);


BEGIN 
 
select	ie_tipo_agendamento 
into STRICT	ie_tipo_agendamento_w 
from	agenda_integrada_item 
where	nr_sequencia	= nr_seq_ageint_item_p;
 
if (ie_tipo_agendamento_w	<> 'C') then 
	nr_seq_regra_w	:= Ageinte_Obter_Seq_Ex_Agenda(nr_seq_Ageint_item_p, cd_estabelecimento_p);
	 
	if (ie_sugestao_exames_p	= 'N') then 
		select	coalesce(max(cd_Agenda),0) 
		into STRICT	cd_agenda_marc_w 
		from	ageint_marcacao_usuario 
		where	Ageinte_Obter_Seq_Ex_Agenda(nr_seq_Ageint_item, cd_estabelecimento_p)	= nr_seq_regra_w 
		and	nm_usuario			= nm_usuario_p 
		and	coalesce(ie_gerado, 'N')		= 'N' 
		and	coalesce(ie_horario_auxiliar, 'N')	= 'N' 
		and	nr_seq_ageint			= nr_seq_ageint_p;
	else 
		select	coalesce(max(cd_Agenda),0) 
		into STRICT	cd_agenda_marc_w 
		from	ageint_sugestao_horarios 
		where	Ageinte_Obter_Seq_Ex_Agenda(nr_seq_item, cd_estabelecimento_p)	= nr_seq_regra_w 
		and	nm_usuario			= nm_usuario_p 
		and	nr_seq_agenda			= nr_seq_ageint_p;
	end if;
	if (cd_Agenda_marc_w	> 0) and (cd_agenda_marc_w	<> cd_agenda_p) then 
		 
		select	ds_agenda 
		into STRICT	ds_agenda_w 
		from	agenda 
		where	cd_agenda	= cd_agenda_marc_w;
		 
		ds_retorno_w	:= Wheb_mensagem_pck.get_texto(307427, 'DS_AGENDA='||ds_agenda_w); -- 'Esse exame deve ser marcado na agenda '||ds_agenda_w||' devido a regras cadastradas.'; 
	end if;
end if;
 
ds_retorno_p	:= ds_retorno_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
