CREATE OR REPLACE PROCEDURE ageint_consultar_medico_espec (nr_seq_ageint_p bigint, nr_seq_ageint_item_p bigint, dt_agenda_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_medico_w		varchar(10);	
nr_seq_ageint_lib_w	bigint;	
ds_erro_w		varchar(255);	
dt_agenda_w		timestamp;		
qt_medico_w		bigint;
cd_agenda_w		bigint;
					
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		cd_pessoa_fisica, 
		cd_agenda 
	from	ageint_lib_usuario 
	where	nr_seq_ageint_item = nr_seq_ageint_item_p;
					

BEGIN 
 
delete	FROM ageint_medico_disp_item 
where	nm_usuario = nm_usuario_p;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_ageint_lib_w, 
	cd_medico_w, 
	cd_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then 
		ds_erro_w := Ageint_regra_qtd_med_espec(nr_seq_ageint_item_p, cd_medico_w, cd_agenda_w, dt_agenda_p, nm_usuario_p, cd_estabelecimento_p, ds_erro_w);
				  
		if (coalesce(ds_erro_w::text, '') = '') then 
		 
			select	count(*) 
			into STRICT	qt_medico_w 
			from	ageint_medico_disp_item 
			where	nr_seq_ageint = nr_seq_ageint_p 
			and	cd_pessoa_fisica = cd_medico_w;
			 
			if (qt_medico_w = 0) then 
			 
				select	min(hr_agenda) 
				into STRICT	dt_agenda_w 
				from	ageint_horarios_usuario 
				where	nr_seq_ageint_lib = nr_seq_ageint_lib_w 
				and	ie_status_agenda = 'L';
				 
				 
				if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then 
				 
					insert into ageint_medico_disp_item( 
									nr_sequencia,      
									dt_atualizacao, 
									nm_usuario,       
									dt_atualizacao_nrec, 
									nm_usuario_nrec,     
									cd_pessoa_fisica,    
									nr_seq_ageint_item,   
									dt_agenda,        
									nr_seq_ageint) 
								values (nextval('ageint_medico_disp_item_seq'), 
									clock_timestamp(), 
									nm_usuario_p, 
									clock_timestamp(), 
									nm_usuario_p, 
									cd_medico_w, 
									nr_seq_ageint_item_p, 
									dt_agenda_w, 
									nr_seq_ageint_p);
				end if;
			end if;
		end if;
	end if;
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
