SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_cons_exec_agenda (cd_tipo_agenda_p bigint, cd_agenda_p bigint, cd_medico_exec_p text, cd_pessoa_fisica_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE


											
ds_consistencia_w		varchar(255) := null;											
nr_seq_regra_w		bigint;
qt_idade_w		smallint := -1;
qt_idade_min_w		smallint := -1;
qt_idade_max_w		smallint := -1;
dt_nascimento_w		timestamp;
ie_idade_regra_w		varchar(1) := 'S';
dt_atual_w		timestamp;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_regra_w
from	agenda_medico
where	cd_agenda = cd_agenda_p
and	cd_medico = cd_medico_exec_p
and	coalesce(ie_situacao,'A') = 'A'
and ((qt_idade_min IS NOT NULL AND qt_idade_min::text <> '') or (qt_idade_max IS NOT NULL AND qt_idade_max::text <> ''));

if (nr_seq_regra_w > 0) then
	/* obter faixa etÃ¡ria */

	select	max(coalesce(qt_idade_min,-1)),
		max(coalesce(qt_idade_max,999))
	into STRICT	qt_idade_min_w,
		qt_idade_max_w
	from	agenda_medico
	where	nr_sequencia = nr_seq_regra_w
	and	coalesce(ie_situacao,'A') = 'A';
	
	if (qt_idade_min_w > -1) or
		(qt_idade_max_w >= 0 AND qt_idade_max_w <> 999) then
		if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
			select	max(dt_nascimento),
				coalesce(max(dt_obito),clock_timestamp())
			into STRICT	dt_nascimento_w,
				dt_atual_w
			from	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_p;
		
			if (dt_nascimento_w IS NOT NULL AND dt_nascimento_w::text <> '') then
				select	coalesce(obter_idade(dt_nascimento_w, dt_atual_w, 'A'),-1)
				into STRICT	qt_idade_w
				;
				
				if (qt_idade_w > -1) then
						select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
						into STRICT	ie_idade_regra_w
						
						where	qt_idade_w between qt_idade_min_w and qt_idade_max_w;
						
						if (ie_idade_regra_w = 'N') then
							if (qt_idade_min_w > -1) and (qt_idade_max_w < 999) then
								ds_consistencia_w :=	substr(wheb_mensagem_pck.get_texto(790637)||chr(10)||
														WHEB_MENSAGEM_PCK.get_texto(790639,'QT_IDADE_MIN_W='||to_char(qt_idade_min_w)||';QT_IDADE_MAX_W='||to_char(qt_idade_max_w))||' '||
														WHEB_MENSAGEM_PCK.get_texto(790638,'QT_IDADE_W='||to_char(qt_idade_w)),1,255);
							elsif (qt_idade_min_w > -1) then
								ds_consistencia_w :=	substr(wheb_mensagem_pck.get_texto(790637)||chr(10)||
														WHEB_MENSAGEM_PCK.get_texto(790640,'QT_IDADE_MIN_W='||to_char(qt_idade_min_w))||' '||
														WHEB_MENSAGEM_PCK.get_texto(790638,'QT_IDADE_W='||to_char(qt_idade_w)),1,255);
							elsif (qt_idade_max_w < 999) then
								ds_consistencia_w :=	substr(wheb_mensagem_pck.get_texto(790637)||chr(10)||
														WHEB_MENSAGEM_PCK.get_texto(790641,'QT_IDADE_MAX_W='||to_char(qt_idade_max_w))||' '||
														WHEB_MENSAGEM_PCK.get_texto(790638,'QT_IDADE_W='||to_char(qt_idade_w)),1,255);									
							end if;
						end if;
				end if;
			end if;
		end if;
	end if;
end if;	

ds_consistencia_p := ds_consistencia_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
