SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ageint_duplicar_item_conv ( nr_seq_ageint_item_p bigint, nr_seq_ageint_item_ret_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ageint_item_w	bigint;
qt_conv_item_w			bigint;	
qt_equip_item_w			bigint;
ie_autorizacao_w		varchar(3);
nr_seq_agenda_cons_w	bigint;
nr_seq_agenda_exame_w	bigint;
		

BEGIN

select	max(nr_seq_agenda_cons),
		max(nr_seq_agenda_exame)		
into STRICT	nr_seq_agenda_cons_w,
		nr_seq_agenda_exame_w		
from	agenda_integrada_item
where	nr_sequencia = nr_seq_ageint_item_p;

if	((nr_seq_agenda_cons_w IS NOT NULL AND nr_seq_agenda_cons_w::text <> '') or (nr_seq_agenda_exame_w IS NOT NULL AND nr_seq_agenda_exame_w::text <> '')) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(277378);
end if;

select	count(*)
into STRICT	qt_conv_item_w
from	agenda_integrada_conv_item
where	nr_seq_agenda_item	= nr_seq_ageint_item_p;

SELECT COUNT(*)
INTO STRICT qt_equip_item_w
FROM AGEINT_EQUIP_ITEM
WHERE nr_seq_agenda_item = nr_seq_ageint_item_p;

if (qt_equip_item_w > 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(685697);
end if;

if (qt_conv_item_w	= 0) then
	select	nextval('agenda_integrada_item_seq')
	into STRICT	nr_seq_ageint_item_w
	;
	
	select	coalesce(max(ie_autorizacao),'L')
	into STRICT	ie_autorizacao_w
	from	agenda_integrada_item
	where	nr_sequencia = nr_seq_ageint_item_p;

	insert into agenda_integrada_item(nr_sequencia,
			nr_seq_agenda_int,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_proc_interno,
			ie_tipo_agendamento,
			cd_medico,
			cd_especialidade,
			nr_seq_agenda_cons,
			nr_seq_agenda_exame,
			cd_profissional_exame,
			nr_minuto_duracao,
			ie_classif_agenda,
			nr_classificacao_agend,
			cd_medico_req,
			dt_entrega_prevista,
			ie_lado,
			ds_observacao,
			nr_seq_grupo_selec,
			nr_seq_classif_agenda,
			nr_seq_item_selec)
		SELECT	nr_seq_ageint_item_w,
			nr_seq_agenda_int,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_proc_interno,
			ie_tipo_agendamento,
			cd_medico,
			cd_especialidade,
			nr_seq_agenda_cons,
			nr_seq_agenda_exame,
			cd_profissional_exame,
			nr_minuto_duracao,
			ie_classif_agenda,
			nr_classificacao_agend,
			cd_medico_req,
			dt_entrega_prevista,
			ie_lado,
			ds_observacao,
			nr_seq_grupo_selec,
			nr_seq_classif_agenda,
			nr_seq_item_selec
		from	agenda_integrada_item
		where	nr_sequencia	= nr_seq_ageint_item_p;
		
	if (ie_autorizacao_w <> 'B') then
		begin
			delete	FROM agenda_integrada_item
			where	nr_sequencia = nr_seq_ageint_item_p;
			
		exception
		when others  then
			begin
			if (replace(SQLSTATE,'-','') = '2292') then
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(686472, 'DS_ERRO=' || sqlerrm );
			end if;
			end;
		end;
	end if;
end if;

commit;

nr_seq_ageint_item_ret_p	:= nr_seq_ageint_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
