CREATE OR REPLACE PROCEDURE ageint_gerar_autor_quimio ( nr_sequencia_p bigint, ie_tipo_agendamento_p text, nm_usuario_p text) AS $body$
DECLARE

				  
qt_agendamento_w	bigint;
nr_sequencia_w		bigint;
ie_tipo_agendamento_w	varchar(15);
nr_Seq_pend_quimio_w	bigint;
nr_Seq_paciente_w	bigint;
nr_ciclo_w		smallint;

c01 CURSOR FOR 
	SELECT	nr_Seq_pend_quimio 
	from	agenda_integrada_item 
	where	nr_seq_agenda_int	= nr_Sequencia_p 
	and	coalesce(ie_tipo_agendamento,'X')	= 'Q' 
	and	coalesce(ie_tipo_pend_Agenda,'X')	= 'Q';
				
 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_Seq_pend_quimio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	min(nr_seq_paciente), 
		min(nr_ciclo) 
	into STRICT	nr_Seq_paciente_w, 
		nr_ciclo_W 
	from	paciente_Atendimento 
	where	nr_seq_pend_agenda	= nr_Seq_pend_quimio_w 
	and 	coalesce(dt_cancelamento::text, '') = '';
	if (nr_Seq_paciente_w IS NOT NULL AND nr_Seq_paciente_w::text <> '') then 
		CALL GERAR_AUTOR_CONV_QUIMIOTERAPIA(nr_seq_paciente_w, nr_ciclo_W, nm_usuario_p,'S');
	end if;
	 
	end;
end loop;
close C01;
 
	 
end;
$body$
