CREATE OR REPLACE PROCEDURE ageint_gerar_bloq_per_angular ( ds_lista_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_motivo_bloqueio_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ds_observacao_p text, ie_dia_semana_p bigint, hr_inicial_p timestamp, hr_final_p timestamp, nr_seq_motivo_bloq_ag_p bigint, ie_bloq_periodo_medic_p text, cd_profissional_p text, cd_convenio_p bigint) AS $body$
DECLARE


ds_lista_w			varchar(4000);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
cd_agenda_w			bigint;
nr_sequencia_w			bigint;


BEGIN

ds_lista_w := ds_lista_p;

while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')  loop
	begin
	tam_lista_w		:= length(ds_lista_w);
	ie_pos_virgula_w		:= position(',' in ds_lista_w);

	if (ie_pos_virgula_w <> 0) then
		cd_agenda_w	:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
		ds_lista_w	:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;


	if (cd_agenda_w IS NOT NULL AND cd_agenda_w::text <> '') then
		if (ie_bloq_periodo_medic_p = 'N') then
			select	nextval('agenda_bloqueio_seq')
			into STRICT	nr_sequencia_w
			;

			insert into agenda_bloqueio(	cd_agenda,
							dt_inicial,
							dt_final,
							ie_motivo_bloqueio,
							dt_atualizacao,
							nm_usuario,
							ds_observacao,
							ie_dia_semana,
							hr_inicio_bloqueio,
							hr_final_bloqueio,
							nr_sequencia,
							qt_agenda_bloq,
							cd_medico,
							nr_seq_motivo_bloq_ag
						)
					values (
							cd_agenda_w,
							dt_inicial_p,
							dt_final_p,
							ie_motivo_bloqueio_p,
							clock_timestamp(),
							nm_usuario_p,
							ds_observacao_p,
							ie_dia_semana_p,
							hr_inicial_p,
							hr_final_p,
							nr_sequencia_w,
							null,
							null,
							nr_seq_motivo_bloq_ag_p
						);
		else
			select	nextval('ageint_bloqueio_medico_seq')
			into STRICT	nr_sequencia_w
			;

			insert into ageint_bloqueio_medico(	cd_agenda,
							dt_inicial,
							dt_final,
							dt_atualizacao,
							nm_usuario,
							ds_observacao,
							ie_dia_semana,
							hr_inicial_bloqueio,
							hr_final_bloqueio,
							nr_sequencia,
							cd_pessoa_fisica,
							dt_Atualizacao_nrec,
							nm_usuario_nrec,
							ie_motivo_bloqueio,
							cd_convenio
						)
					values (
							cd_agenda_w,
							dt_inicial_p,
							dt_final_p,
							clock_timestamp(),
							nm_usuario_p,
							ds_observacao_p,
							ie_dia_semana_p,
							hr_inicial_p,
							hr_final_p,
							nr_sequencia_w,
							cd_profissional_p,
							clock_timestamp(),
							nm_usuario_p,
							ie_motivo_bloqueio_p,
							cd_convenio_p
						);
		end if;
	end if;

	end;
	end loop;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
