CREATE OR REPLACE PROCEDURE ageint_gerar_consulta_agecons2 (cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE



cd_especialidade_w		bigint;
cd_medico_exec_w	varchar(10);
cd_pessoa_fisica_w	varchar(10);
nm_paciente_w		varchar(60);
ie_status_Agenda_w	varchar(3);
nr_minuto_duracao_w	bigint;
dt_agenda_w		timestamp;
nr_seq_agenda_w		agenda_consulta.nr_sequencia%TYPE;
nr_seq_status_pac_w	bigint;
nr_atendimento_w		bigint;
cd_convenio_w		integer;
hr_inicial_intervalo_w	timestamp;
hr_final_intervalo_w		timestamp;
cd_turno_ww		varchar(1);
ie_encaixe_w		varchar(1);
ie_classif_Agenda_w	varchar(0005);
qt_hor_w		bigint;
ie_feriado_w		varchar(1);
ie_sobra_horario_w	varchar(1);
ds_horarios_w		varchar(255);
ds_motivo_w			varchar(255);

C03 CURSOR FOR
	SELECT	cd_pessoa_fisica,
			substr(nm_paciente,1,60),
			cd_medico_req,
			ie_status_agenda,
			nr_minuto_duracao,
			dt_Agenda,
			nr_sequencia,
			nr_seq_status_pac,
			nr_atendimento,
			cd_convenio,
			cd_turno,
			coalesce(ie_encaixe,'N'),
			ie_classif_agenda,
			SUBSTR(obter_motivo_agecons(nr_sequencia,ie_status_agenda,coalesce(nr_seq_motivo_transf,somente_numero(cd_motivo_cancelamento))),1,255)
	from	agenda_consulta
	where	cd_agenda		= cd_agenda_p
	and		trunc(dt_agenda)	= trunc(dt_agenda_p)
	order by dt_agenda;
	

BEGIN

/*gerar_horario_agenda_consulta(nm_usuario_p, cd_estabelecimento_p, cd_agenda_p, dt_agenda_p, null, null, 0);*/

select	coalesce(ie_feriado,'N'),
	coalesce(ie_gerar_sobra_horario,'N')
into STRICT	ie_feriado_w,
	ie_sobra_horario_w
from	agenda
where	cd_agenda = cd_agenda_p;

ds_horarios_w := horario_livre_consulta(cd_estabelecimento_p, cd_agenda_p, ie_feriado_w, dt_agenda_p, nm_usuario_p, 'S', ie_sobra_horario_w, 'N', 0, ds_horarios_w);	

delete	FROM ageint_consulta_horarios
where	cd_agenda	= cd_agenda_p
and	nm_usuario	= nm_usuario_p
and	dt_agenda	between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 83699/86400;

select	max(cd_especialidade)
into STRICT	cd_especialidade_w
from	agenda
where	cd_agenda	= cd_agenda_p;

open C03;
loop
fetch C03 into	
	cd_pessoa_fisica_w,
	nm_paciente_w,
	cd_medico_exec_w,
	ie_status_agenda_w,
	nr_minuto_duracao_w,
	dt_agenda_w,
	nr_seq_agenda_w,
	nr_seq_status_pac_w,
	nr_atendimento_w,
	cd_convenio_w,
	cd_turno_ww,
	ie_encaixe_w,
	ie_classif_agenda_w,
	ds_motivo_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin
	
	insert into ageint_consulta_horarios(dt_Atualizacao,
		nm_usuario,
		cd_agenda,
		cd_medico_exec,
		cd_paciente,
		nm_paciente,
		ie_status_agenda,
		nr_minuto_duracao,
		dt_agenda,
		nr_seq_agenda,
		nr_seq_status_pac,
		nr_atendimento,
		cd_convenio,
		cd_turno,
		ie_encaixe,
		cd_especialidade,
		ie_classif_agenda,
		ds_motivo)
	values (clock_timestamp(),
		nm_usuario_p,
		cd_Agenda_p,
		cd_medico_exec_w,
		cd_pessoa_fisica_w,
		nm_paciente_w,
		ie_status_Agenda_w,
		nr_minuto_duracao_w,
		dt_agenda_w,
		nr_seq_agenda_w,
		nr_seq_status_pac_w,
		nr_atendimento_w,
		cd_convenio_w,
		cd_turno_ww,
		ie_encaixe_w,
		cd_especialidade_w,
		ie_classif_agenda_w,
		ds_motivo_w);
	end;
end loop;
close C03;

commit;

end;
$body$
