CREATE OR REPLACE PROCEDURE ageint_gerar_consulta_ageexam2 (cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE

			
/* variaveis de registro */

nr_seq_agenda_w			agenda_paciente.nr_sequencia%TYPE;
nr_minuto_duracao_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_proc_interno_w	bigint;
ie_encaixe_w			varchar(1);
cd_turno_ww				varchar(1);
cd_medico_exec_w		varchar(10);
cd_pessoa_fisica_w		varchar(10);
nm_paciente_w			varchar(60);
ie_status_Agenda_w		varchar(3);
hr_inicio_w				timestamp;
nr_seq_medico_regra_w	bigint;
nr_seq_status_pac_w		bigint;
nr_atendimento_w		bigint;
cd_convenio_ww			integer;
qt_horarios_w			bigint;
ds_motivo_w			varchar(255);
	
C06 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		nm_paciente,
		cd_medico_exec,
		ie_status_agenda,
		nr_minuto_duracao,
		hr_inicio,
		nr_seq_status_pac,
		cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno,
		nr_sequencia,
		nr_atendimento,
		cd_convenio,
		cd_turno,
		coalesce(ie_encaixe,'N'),
		SUBSTR(obter_motivo_agenda(nr_sequencia),1,255)
	from	agenda_paciente
	where	cd_agenda		= cd_agenda_p
	and	trunc(dt_agenda)	= trunc(dt_agenda_p)
	order by hr_inicio;


BEGIN

delete	FROM ageint_consulta_horarios
where	cd_agenda	= cd_agenda_p
and	nm_usuario	= nm_usuario_p
and	dt_agenda	between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 83699/86400;

CALL gerar_horario_agenda_exame(cd_estabelecimento_p,
	cd_agenda_p,
	dt_agenda_p,
	nm_usuario_p);

open C06;
loop
fetch C06 into	
	cd_pessoa_fisica_w,
	nm_paciente_w,
	cd_medico_exec_w,
	ie_status_agenda_w,
	nr_minuto_duracao_w,
	hr_inicio_w,
	nr_seq_status_pac_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w,
	nr_seq_agenda_w,
	nr_atendimento_w,
	cd_convenio_ww,
	cd_turno_ww,
	ie_encaixe_w,
	ds_motivo_w;
EXIT WHEN NOT FOUND; /* apply on C06 */
	begin
	
	select	max(nr_seq_medico_regra)
	into STRICT	nr_seq_medico_regra_w
	from	agenda_medico
	where	cd_agenda	= cd_agenda_p
	and	cd_medico	= cd_medico_exec_w;
	
	insert into ageint_consulta_horarios(dt_Atualizacao,
		nm_usuario,
		cd_agenda,
		cd_medico_exec,
		cd_paciente,
		nm_paciente,
		ie_status_agenda,
		nr_minuto_duracao,
		dt_agenda,
		nr_seq_medico_regra,
		nr_seq_status_pac,
		cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno,
		nr_seq_agenda,
		nr_atendimento,
		cd_convenio,
		cd_turno,
		ie_encaixe,
		ds_motivo)
	values (clock_timestamp(),
		nm_usuario_p,
		cd_Agenda_p,
		cd_medico_exec_w,
		cd_pessoa_fisica_w,
		nm_paciente_w,
		ie_status_Agenda_w,
		nr_minuto_duracao_w,
		hr_inicio_w,
		nr_seq_medico_regra_w,
		nr_seq_status_pac_w,
		cd_procedimento_w,
		ie_origem_proced_w,
		nr_seq_proc_interno_w,
		nr_seq_agenda_w,
		nr_atendimento_w,
		cd_convenio_ww,
		cd_turno_ww,
		ie_encaixe_w,
		ds_motivo_w);
	end;
end loop;
close C06;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
