CREATE OR REPLACE PROCEDURE ageint_gerar_consulta_ageserv2 (cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
nm_paciente_w		varchar(80);
cd_medico_exec_w	varchar(10);
ie_status_Agenda_w	varchar(3);
nr_minuto_duracao_w	bigint;
dt_agenda_w		timestamp;
ie_classif_agenda_origem_w 	varchar(5);
ie_classif_agenda_ww		varchar(5);
ds_observacao_agenda_w	varchar(2000);
nr_atendimento_w	bigint;
cd_convenio_w		integer;
nr_seq_agenda_w			agenda_consulta.nr_sequencia%TYPE;
cd_turno_w			varchar(01);
cd_procedimento_w		bigint;
nr_seq_proc_interno_w		bigint;
ie_origem_proced_w		bigint;
ie_encaixe_w		varchar(1);
ds_motivo_w			varchar(255);

C03 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		nm_paciente,
		cd_medico,
		ie_status_agenda,
		nr_minuto_duracao,
		dt_Agenda,
		ie_classif_agenda_origem,
		ie_classif_agenda,
		ds_observacao,
		nr_atendimento,
		cd_convenio,
		nr_sequencia,
		cd_turno,
		cd_procedimento,
		nr_seq_proc_interno,
		ie_origem_proced,
		coalesce(ie_encaixe,'N'),
		SUBSTR(obter_motivo_agecons(nr_sequencia,ie_status_agenda,coalesce(nr_seq_motivo_transf,somente_numero(cd_motivo_cancelamento))),1,255)
	from	agenda_consulta
	where	cd_agenda		= cd_agenda_p
	and	trunc(dt_agenda)	= trunc(dt_agenda_p)
	order by dt_agenda;
	

BEGIN

CALL Gerar_Horario_Agenda_Servico(cd_estabelecimento_p, cd_agenda_p, dt_agenda_p, nm_usuario_p);

delete	FROM ageint_consulta_horarios
where	cd_agenda	= cd_agenda_p
and	nm_usuario	= nm_usuario_p
and	dt_agenda	between trunc(dt_agenda_p) and trunc(dt_agenda_p) + 83699/86400;

open C03;
loop
fetch C03 into	
	cd_pessoa_fisica_w,
	nm_paciente_w,
	cd_medico_exec_w,
	ie_status_agenda_w,
	nr_minuto_duracao_w,
	dt_agenda_w,
	ie_classif_agenda_origem_w,
	ie_classif_agenda_ww,
	ds_observacao_agenda_w,
	nr_atendimento_w,
	cd_convenio_w,
	nr_seq_agenda_w,
	cd_turno_w,
	cd_procedimento_w,
	nr_seq_proc_interno_w,
	ie_origem_proced_w,
	ie_encaixe_w,
	ds_motivo_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin
	
	insert into ageint_consulta_horarios(dt_Atualizacao,
		nm_usuario,
		cd_agenda,
		cd_medico_exec,
		cd_paciente,
		nm_paciente,
		ie_status_agenda,
		nr_minuto_duracao,
		dt_agenda,
		ie_classif_agenda_origem,
		ie_classif_agenda,
		ds_observacao,
		nr_seq_agenda,
		nr_atendimento,
		cd_convenio,
		cd_turno,
		cd_procedimento,
		nr_seq_proc_interno,
		ie_origem_proced,
		ie_encaixe,
		ds_motivo)
	values (clock_timestamp(),
		nm_usuario_p,
		cd_Agenda_p,
		cd_medico_exec_w,
		cd_pessoa_fisica_w,
		substr(nm_paciente_w,1,60),
		ie_status_Agenda_w,
		nr_minuto_duracao_w,
		dt_agenda_w,
		ie_classif_agenda_origem_w,
		ie_classif_agenda_ww,
		ds_observacao_agenda_w,
		nr_seq_agenda_w,
		nr_atendimento_w,
		cd_convenio_w,
		cd_turno_w,
		cd_procedimento_w,
		nr_seq_proc_interno_w,
		ie_origem_proced_w,
		ie_encaixe_w,
		ds_motivo_w);
	end;
end loop;
close C03;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
