CREATE OR REPLACE PROCEDURE ageint_gerar_prof_grupo ( nr_seq_grupo_p bigint, nr_seq_local_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_prof_regra_w	bigint;
qt_profissional_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	qt_grupo_profissional a,
		qt_profissional b
	where	a.nr_sequencia	= b.nr_seq_grupo
	and	coalesce(b.cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	and	coalesce(b.ie_situacao,'I') = 'A'
	and	a.nr_sequencia	= nr_seq_grupo_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_prof_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_profissional_w
	from	qt_prof_local
	where	nr_seq_prof	= nr_seq_prof_regra_w
	and	nr_seq_local	= nr_seq_local_p;

	if (qt_profissional_w	= 0) then
		insert into qt_prof_local(nr_sequencia,
					dt_Atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_prof,
					nr_seq_local)
				values (nextval('qt_prof_local_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_prof_regra_w,
					nr_seq_local_p);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
