CREATE OR REPLACE PROCEDURE ageint_gerar_tipo_quimio_grupo ( nr_seq_grupo_p bigint, nr_seq_local_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_prof_regra_w	bigint;
qt_item_local_w	bigint;
ie_tipo_pend_agenda_w	varchar(3);

C01 CURSOR FOR
	SELECT	b.cd_item
	from	qt_grupo_item_agenda a,
		qt_item_Agenda b
	where	a.nr_sequencia	= b.nr_seq_grupo
	and	coalesce(a.cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p
	and	a.nr_sequencia		= nr_seq_grupo_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	ie_tipo_pend_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_item_local_w
	from	qt_tipo_quimio_local
	where	ie_tipo_pend_Agenda	= ie_tipo_pend_Agenda_w
	and	nr_seq_local		= nr_seq_local_p;

	if (qt_item_local_w	= 0) then
		insert into qt_tipo_quimio_local(nr_sequencia,
					dt_Atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ie_tipo_pend_Agenda,
					nr_seq_local,
					nr_seq_grupo_quimio)
				values (nextval('qt_tipo_quimio_local_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					ie_tipo_pend_agenda_w,
					nr_seq_local_p,
					nr_seq_grupo_p);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
