CREATE OR REPLACE PROCEDURE ageint_gerar_turno_grupo_proc ( nr_seq_grupo_proc_p bigint, dt_dia_semana_p bigint, dt_inicial_vigencia_p timestamp, dt_final_vigencia_p timestamp, hr_inicial_p text, hr_final_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_interno_w	bigint;
hr_inicial_w		timestamp;
hr_final_w		timestamp;

C01 CURSOR FOR
	SELECT	nr_seq_proc_interno
	from	ageint_proced_regra
	where	nr_seq_grupo		= nr_seq_grupo_proc_p
	and	coalesce(ie_situacao, 'A')	= 'A'
	order by 1;


BEGIN

hr_inicial_w	:= pkg_date_utils.get_time(clock_timestamp(), hr_inicial_p, 0);
hr_final_w	:= pkg_date_utils.get_time(clock_timestamp(), hr_final_p, 0);

open C01;
loop
fetch C01 into
	nr_seq_proc_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into ageint_turno_exame(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_proc_interno,
		dt_inicial_vigencia,
		dt_final_vigencia,
		hr_inicial,
		hr_final,
		dt_dia_semana,
		ie_consiste_marcacao)
	values (nextval('ageint_turno_exame_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_proc_interno_w,
		dt_inicial_vigencia_p,
		dt_final_vigencia_p,
		hr_inicial_w,
		hr_final_w,
		dt_dia_semana_p,
		'N');
	end;
end loop;
close C01;

commit;

end;
$body$
