CREATE OR REPLACE PROCEDURE ageint_gravar_log_checklist ( ds_lista_p text, nr_seq_ageint_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_w			varchar(4000);
ds_email_w			varchar(2000);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
nr_seq_log_checklist_w		bigint;


BEGIN
ds_lista_w := ds_lista_p;

while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '')  loop
	begin
	tam_lista_w		:= length(ds_lista_w);
	ie_pos_virgula_w	:= position(';' in ds_lista_w || ';');

	if (ie_pos_virgula_w <> 0) then
		ds_email_w	:= substr(ds_lista_w,1,(ie_pos_virgula_w - 1));
		ds_lista_w	:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	select	nextval('ageint_log_envio_checklist_seq')
	into STRICT	nr_seq_log_checklist_w
	;

	if (ds_email_w IS NOT NULL AND ds_email_w::text <> '') then
		insert into ageint_log_envio_checklist(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ds_email,
					nr_seq_ageint)
			values (
				nr_seq_log_checklist_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_email_w,
				nr_seq_ageint_p);
	end if;
	end;
end loop;

commit;

end;
$body$
